insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7523, 'Hulchul', False, True, False, False, 2004, 6.9, 'A man and woman from feuding families each pretend to fall in love, as part of a revenge plot. Chaos ensues when their fake romance becomes a reality.', 'tt0437238', '/ve6YjkjNAbY2r2djFxrw6v9IXfy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7524, 'Mythica: The Iron Crown', False, True, False, False, 2016, 5.4, 'When a team of unlikely heroes hijacks a steam-powered battle wagon, a daring young wizard (Marek) steals the final piece of the all-powerful Darkspore and embarks on a desperate quest to deliver the cursed artifact to the gods for safe keeping; but when they are caught in a death race between a ruthless team of elite mercenaries and a trinity of demons, Marek must learn to believe in herself before her friends are killed and the Darkspore is lost, to stop the evil necromancer (Szorlok) from uniting the Darkspore and flooding the living world with his legions of undead.', 'tt5456798', '/8C1H8axr02gTIzNllPKfylKmHfu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7525, 'Hansel and Gretel Get Baked', False, True, False, False, 2013, 4.3, 'An intense new marijuana strain named “Black Forest” is taking Los Angeles by storm, and Gretel’s stoner boyfriend can’t get enough. But when the old woman growing the popular drug (Lara Flynn Boyle) turns out to be an evil witch, cooking and eating her wasted patrons for their youth, Gretel and her brother Hansel must save him from a gruesome death — or face the last high of their lives.', 'tt2081194', '/shYb3LezqJF4P90CGgp9zoD2MjF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7526, '33 Postcards', False, True, False, False, 2011, 6.2, 'Dean Randall has sponsored a young Chinese orphan Mei Mei for many years, when she arrives in Sydney out of the blue to thank him, their lives are changed forever.', 'tt1704161', '/lFXCqrtAGO5w41cYxoDaJR9gzXJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7527, 'Loverboy', False, True, False, False, 2005, 5.4, 'A neglected daughter becomes a possessive mother in an emotional journey into the heart and mind of a woman who loved too much.', 'tt0388213', '/vziSkZ47MA1GvQk1oBd7jJSDh6l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7528, 'Babysitter Wanted', False, True, False, False, 2009, 5.4, 'When she takes a job babysitting a young boy (Kai Caster) for a night at his family''s remote farmhouse, sweet college co-ed Angie Albright (Sarah Thompson) becomes the target of a scar-covered creep making mysterious phone calls and prowling outside the windows. Angie gets the drop on the would-be killer, but quickly discovers that her nightmare has just begun.', 'tt0819755', '/7DQlbb4ax5L5NZvMjr54KOsXGyF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7532, 'Born Innocent', False, True, False, False, 1974, 6.4, 'A constant runaway is given over to the care of the state and finds herself in a remand centre for girls. She is soon caught between the uncaring bureaucracy, the sometimes brutal treatment from her peers and her own abusive family, and only one care worker sees her potential to rise above her tragic circumstances.', 'tt0071240', '/siPHxStqUGsWc6gLOfSs7YzEMnp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7533, 'Mediastan', False, True, False, False, 2013, 7.8, 'A small group of Wikileaks journalists make their way through Central Asia interviewing newspaper editors. Their real goal: to find local media outlets to publish secret US diplomatic cables. This intelligent, guerrilla-style doc follows their fascinating journey from Afghanistan to Manhattan, through the boundaries of free speech and the minds of those who shape our understanding of the world.', 'tt3169780', '/dwoq7yDXK3NCgXS2jQMrX5mInmC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7534, 'Saints and Soldiers: Airborne Creed', False, True, False, False, 2012, 5.7, 'A group of American GIs work their way through war-torn France during the final days of the Second World War.', 'tt2082415', '/gwqR9UY0xqBZwP2qb8ZPmf9b2lq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7535, 'Dunkirk', False, True, False, False, 2004, 7.2, 'The miraculous evacuation of Allied soldiers from Belgium, Britain, Canada and France, who were cut off and surrounded by the German army from the beaches and harbor of Dunkirk, France, between May 26th and June 4th 1940, during the Battle of France in World War II.', 'tt5013056', '/ebSnODDg9lbsMIaWg2uAbjn7TO5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7536, 'The Tune', False, True, False, False, 1992, 7.1, 'Del is a song writer for the obnoxious Mr. Mega, and in love with Didi, Mega''s secretary. His quest to write a hit tune brings him to the wacky world of Flooby Nooby, where he just might learn to write songs from the heart.', 'tt0105654', '/7zLlA4nUMEFbkrYp64KmGZhijMi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7539, 'My Boss''s Daughter', False, True, False, False, 2003, 4.7, 'When a young man agrees to housesit for his boss, he thinks it''ll be the perfect opportunity to get close to the woman he desperately has a crush on – his boss''s daughter. But he doesn''t plan on the long line of other houseguests that try to keep him from his mission. And he also has to deal with the daughter''s older brother, who''s on the run from local drug dealers.', 'tt0270980', '/eZsFXr3m0shC5HH7SRlZouiuQVH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7545, 'Against All Flags', False, True, False, False, 1952, 6.6, 'British Naval Officer (Errol Flynn) fights pirates on Madagascar.', 'tt0044333', '/19g5Z9lXYSgsO568Zunl5KfwRmF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7546, 'Frightmare', False, True, False, False, 1974, 6.3, 'In 1957, Dorothy and Edmund Yates (Keith and Rupert Davies) were committed to an institution for the criminally insane, she for acts of murder and cannibalism and he for covering up her crimes. Fifteen years later, they are pronounced fit for society and released. However, in Dorothy''s case the doctors may have jumped the gun a bit. Edmund and eldest daughter, Jackie (Deborah Fairfax), try to discover just how far Mother''s bloodlust has taken her. Meanwhile, youngest daughter Debbie (Kim Butcher) begins to explore the crazy roots of her family tree as fully as possible.', 'tt0071523', '/9lJdYMIMpPIdTBVoSsFIm3e6cVQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7547, 'Zombie Ninjas vs Black Ops', False, True, False, False, 2015, 6.8, 'In the clandestine research tower of a ruthless Japanese security and arms company, Saisei Security, scientist MALI awakens employed mercenaries killed in combat, their death giving rise to a new, vicious breed of zombie... ZOMBIE NINJAS. Trapped amongst unsuspecting civilians, including former tactical operative DILLON (Adam T Perkins), the ZOMBIE NINJAS commanded by a ferocious leader (Soa ''The Hulk'' Palelei) are unleashed in the inner city high-rise. They soon face off with the covert response mission deployed to fight back. This elite black ops team underestimates the brutality of their encounter, which ultimately tests their loyalty and friendship, resulting in a frightful assault between these two highly skilled lethal forces and DILLON thrust in the middle fighting for survival.', 'tt3397948', '/e1E2YMpju9NU7SewuzyX3KcQGrm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7549, 'Treading Water', False, True, False, False, 2013, 6.1, 'At first glance, Mica seems a perfectly normal boy. But first glances can often be deceiving... For one, Mica''s house is now a museum honouring Guillermo Garibai, the legendary Mexican crooner. Mica spends most of his time there, giving guided tours to aging Garibai fans. But stranger still, Mica smells. He smells like fish. Numerous doctors, his life-long therapist and even his own parents are at a loss. No one wants to be Mica''s friend. Girls won''t talk to him. His life appears pointless, uneventful, doomed. That is, until Laura walks into it.', 'tt2091427', '/z15C6sb1RwuMnQh4egoWFdwbGpZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7550, 'Glen or Glenda', False, True, False, False, 1953, 4.2, 'A psychiatrist tells two stories: one of a transvestite (Glen or Glenda), the other of a pseudohermaphrodite (Alan or Anne).', 'tt0045826', '/11OHFSacx5CEP6lkn12sO6zyaw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7551, 'Othello', False, True, False, False, 2001, 7.3, 'Iago and a comrade-in-arms are outside the Venice home of Desdemona''s father, who does not yet know that she has eloped with Othello. Iago confides to his friend -- who had hoped to marry Desdemona -- that he serves Othello to further his own ends. Venice needs Othello to protect its commercial interests in Cyprus where the Turkish fleet is headed. Desdemona insists on going to Cyprus, too. In Cyprus, Iago plots to convince Othello that Desdemona has betrayed him with Cassio. A lot more than political ambition seems to be motivating Iago.', 'tt0082861', '/745zwXiUSl7HM4SETJUL0GIabHy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7552, 'Sacrifice', False, True, False, False, 2016, 5.3, 'Sacrifice is the story of consultant surgeon, Tora Hamilton, who moves with her husband, Duncan, to the remote Shetland Islands, 100 miles off the north-east coast of Scotland. Deep in the peat soil around her new home, Tora discovers the body of a young woman with rune marks carved into her skin and a gaping hole where her heart once beat. Ignoring warnings to leave well alone, Tora uncovers terrifying links to a legend that might never have been confined to the pages of the story-books.', 'tt2078718', '/eVrlNY7SbWmTslaRlJvo3sMsZm4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7553, 'In Vogue: The Editor''s Eye', False, True, False, False, 2012, 7.3, 'Documentary film takes a look at some of the world''s most influential fashion images as conceived by the magazine''s iconic fashion editors.', 'tt2476624', '/jzBhQ7RJkaOt0uqxejg1zuDxV4U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7554, 'Eurocrime! The Italian Cop and Gangster Films That Ruled the ''70s', False, True, False, False, 2012, 7.7, 'A documentary concerning the violent Italian ''poliziotteschi'' cinematic movement of the 1970s which, at first glance, seem to be rip-offs of American crime films like DIRTY HARRY or THE GODFATHER, but which really address Italian issues like the Sicilian Mafia and red terrorism. Perhaps even more interesting than the films themselves were the rushed methods of production (stars performing their own stunts, stealing shots, no live sound) and the bleed-over between real-life crime and movie crime.', 'tt2015367', '/brSY5LkXQo0cpqOcZU7ABBntPAO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7555, 'Reaper', False, True, False, False, 2015, 5.7, 'Criminals and a beautiful but cunning hitchhiker must battle a supernatural force known as the Reaper.', 'tt3081268', '/626nJ2yQYl6aUyh0Zn8OV1qZTMM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7556, 'Hungry Wives', False, True, False, False, 1972, 5.5, 'A bored, unhappy suburban housewife gets mixed up in witchcraft and murder.', 'tt0069239', '/hhBfST0YjRpTrLbB6XH0HXoXmkA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7557, 'Killswitch', False, True, False, False, 2015, 7.8, 'This Internet is under attack. Communications, culture, free speech, innovation, and democracy are all up for grabs. Will the Internet be dominated by a few powerful interests? Or will citizens rise up to protect it?', 'tt4067162', '/oOFizg5CTL4GgD1I54ZOq2DZfNp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7559, 'The Big Bad Swim', False, True, False, False, 2006, 6.3, 'An adult swim class sets the stage for this sexually charged comedy-drama about love, loss, and second chances. Three strangers -- inextricably connected through personal heartbreak -- find themselves entering deep water as they learn that life isn''t always about diving right in -- it''s about getting your feet wet once in awhile.', 'tt0460721', '/5zDACIUPPzlD5oNaKDwLZgJEebv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7560, 'Evidence', False, True, False, False, 2013, 5.4, 'Ryan is making a documentary on his friend, Brett, about camping for the first time. However, once they begin camping, they discover that there is a mysterious figure that is hunting them.', 'tt1640218', '/4bloj9VOXaZwoDqkdse5aq5pVZn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7561, 'Witch''s Night Out', False, True, False, False, 1978, 7.6, 'A witch, disgruntled by the fact that no one takes Halloween seriously anymore, decides to stir things up and disrupt the social gathering in her old house as well as turn a couple of kids who love monsters into actual monsters.', 'tt0078500', '/iT1I06kJyQlU6GDZrmOxzxCsR0m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7565, 'The Boys from County Clare', False, True, False, False, 2003, 6.5, 'Two feuding brothers and their respective Ceilidh bands compete at a music festival. Set in 1960''s Ireland.', 'tt0337631', '/3pO8Ur0tfOlBr2m9zJxMMm5Ha1F.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7566, 'Three Wishes', False, True, False, False, 1995, 6.1, 'While Jane Holman (Mastrantonio) is driving with her two sons, she accidentally runs into a drifter, Jack McCloud (Swayze), who breaks his leg. Being responsible,  Jane invites Jack, and his dog, to stay at her home until his leg has healed.  Jack struggles to adapt their lifestyle, and finds himself loved by the family. He starts teaching baseball to Tom, who misses his father, who was lost in the Korean war. Jack and Tom develop a strong bond of friendship. Meanwhile, Gunny believes that there is more to Jack and Betty Jane than meets the eye...  We learn that Jack, is Jack McCloud, a Star White Socks baseball player in 1941, who dropped out of the league, after his first season, and; "was never heard from again" ...  A wonderful story.  We witness magic between a boy''s imagination, and Jack''s dog, and are never sure if we are witnessing imagination or magic by the dog.  A story of friendship, family, and learning that life isn''t always as cut and dried as we often believe.', 'tt0114663', '/qY1Mz8bPGKYmrb4kY1zm7s4480V.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7567, 'The Conscientious Objector', False, True, False, False, 2004, 8.3, 'It''s 1945, World War II. The Place, Okinawa. The Scene, an impregnable 400-foot high cliff-AKA Hacksaw Ridge. The Engagement, a battle so fierce the odds of survival were 1 in 10. The Act, Medic Pfc. Desmond T. Doss braved intense enemy fire to rescue 75 wounded GI''s over the precipice. The Story, Infantrymen who once ridiculed and scoffed at Desmond''s simple faith and refusal to carry a weapon-now owed their lives to him. Director Terry Benedict tells Desmond''s incredible story through the eyes of the men who witnessed this humble man''s heroic acts. Winning the respect of his fellow soldiers, they recommended him for the highest honor America can bestow on one of her sons-The Medal of Honor.', 'tt0302427', '/99rWKXmSDCpgieNPJE1o5cwfVhm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7569, 'Attack of the Giant Leeches', False, True, False, False, 1959, 3.7, 'In a community nearby a swamp, a local dweller sees a couple of giant monsters but nobody believes on his words. Later, the cuckold Dave Walker (Bruno Ve Sota) finds his slut wife Liz Walker (Yvette Vickers) cheating him with his friend Cal Moulton (Michael Emmett). He chases the couple through the swamp, and forces them to jump into the water. The leeches attack them, and the shocked Dave is arrested, accused of murder. Two other locals decide to look for the bodies of Liz and Cal to get the prize of US$ 50.00 per body, and also vanish in the swamp. Finally, Sheriff Steve Benton (Ken Clark) organizes a patrol to investigate the caves under the swamp, finding the lethal giant leeches.', 'tt0053611', '/tTuK2rBQBe8QqYcW533TYuOsyeT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7570, 'Moving Midway', False, True, False, False, 2007, 7.3, 'When New York film critic Godfrey Cheshire returns home to North Carolina in early 2004 and hears that his cousin Charlie Silver plans to uproot and move the buildings of Midway Plantation, their family’s ancestral home, an extraordinary, emotional journey begins.', 'tt1051245', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7571, 'Dead of Night', False, True, False, False, 1977, 6.3, 'A young soldier is killed in the line of duty in Vietnam. That same night, he returns home, brought back by his mother''s wishes. Upon his return, Andy sits in his room, refusing to see his friends or family, venturing out only at night. The vampiric horror is secondary to the terror that comes from the disintegration of a typical American family.', 'tt0068457', '/4tpEk3gWE8PM4MjlcGuHyfil5Uf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7572, 'Thunder Alley', False, True, False, False, 1985, 6.8, 'A group of friends start a rock band, but as they start their rise in the music world, they get mixed up with drugs.', 'tt0090168', '/gR774fP3sSdj2gQeDwGEd4YH5nv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7575, 'Sethupathi', False, True, False, False, 2016, 7.2, 'Sethupathi, an inspector, investigates the murder of a cop and finds that Vaathiyar, a big shot, is responsible for the killing. He arrests Vaathiyar, but an unfortunate incident that has a connection to the case he is investigating, puts his career in jeopardy.', 'tt5491920', '/wsJ165gSPgtWK4lkch3bxFuFzMu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7576, 'Main Khiladi Tu Anari', False, True, False, False, 1994, 6.5, 'A Bombay police officer seeks revenge when his brother is killed by a powerful underworld figure. Things get complicated when a matinee movie idol begins to tag along the policeman so he can research a movie role.', 'tt0110438', '/dP9B3si2RqdLW5cUULWPKmCUujd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7577, 'Something Ventured', False, True, False, False, 2011, 7, 'Apple. Intel. Genentech. Atari. Google. Cisco. Stratospheric successes with high stakes all around. Behind some of the world''s most revolutionary companies are a handful of men who (through timing, foresight, a keen ability to size up other people, and a lot of luck) saw opportunity where others did not: these are the original venture capitalists. All were backing and building companies before the term ''venture capital'' had been coined: companies that led to the birth of biotechnology and the spectacular growth in microprocessors, personal computers and the web. SOMETHING VENTURED uncovers the ups and downs of the building of some of the greatest companies of the twentieth century, and the hidden dramas behind some of the most famous names in business.', 'tt1737747', '/pbUvWUJUcCBad4mEGlRFqEYfX29.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7578, 'An American Girl: Chrissa Stands Strong', False, True, False, False, 2009, 6.2, 'A fourth grader and her friends deal with bullying from a more popular girl in their class.', 'tt1340418', '/zBrOirXNgvm2YsaydZhZCEiKKPJ.jpg');
