insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7589, 'The Botany of Desire', False, True, False, False, 2009, 7.6, 'Featuring Michael Pollan and based on his best-selling book, this special takes viewers on an exploration of the human relationship with the plant world -- seen from the plants'' point of view. Narrated by Frances McDormand, the program shows how four familiar species -- the apple, the tulip, marijuana and the potato -- evolved to satisfy our yearnings for sweetness, beauty, intoxication.', 'tt1421383', '/v1kmd891MI9jMZcEXSQyN8emFNJ.jpg');
