insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7622, 'Man About Town', False, True, False, False, 2006, 5.5, 'A top Hollywood talent agent finds his cushy existence threatened when he discovers that his wife is cheating on him and that his journal has been swiped by a reporter out to bring him down.', 'tt0420757', '/a0h6qRXqum1BSWxTnLZ5IHJZjnc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7624, 'Kingdom of Shadows', False, True, False, False, 2015, 6.7, 'KINGDOM OF SHADOWS follows three people grappling with the hard choices and destructive consequences of the U.S.-Mexico “drug war”. Filmmaker Bernardo Ruiz weaves together the seemingly disconnected stories of an activist nun in deeply scarred Monterrey, Mexico, a U.S. Federal agent on the border, and a former Texas smuggler to reveal the human side of an often-misunderstood conflict that has resulted in the “disappearance” of more than 23,000 people in Mexico—a growing human rights crisis that only recently has made international headlines.', 'tt3785740', '/9w1hJtPkjLibJFN66uFd8J6ChrC.jpg');
