insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7631, 'Manufacturing Dissent', False, True, False, False, 2007, 5.7, '"Michael Moore doesn''t like documentaries. That''s why he doesn''t make them." A documentary that looks to distinguish what''s fact, fiction, legend, and otherwise as a camera crew trails Michael Moore as he tours with his film, Fahrenheit 9/11.', 'tt0961117', '/h43w3x4EsUqV9djlybzl8sXKu9P.jpg');
