insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7687, 'Virus', False, True, False, False, 1980, 6.2, 'When the crew of an American tugboat boards an abandoned Russian research vessel, the alien life form aboard regards them as a virus which must be destroyed.', 'tt0120458', '/5Np88iigCfnXvMlMKmCcbPVIbYQ.jpg');
