insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7740, 'Coda', False, True, False, False, 2013, 7.2, 'A lost soul stumbles drunken through the city. In a park, Death finds him and shows him many things.', 'tt3350724', '/g4sTN0q7ndnjccIhdpGHZ7q2p8c.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7741, 'The Woodcarver', False, True, False, False, 2012, 6.8, 'A troubled youth vandalizes a church and winds up in a close association with the woodcarver whose work he destroyed.', 'tt2047890', '/6pXztdtYhuQApAZIFvudDqCwCwO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7742, 'Art of the Prank', False, True, False, False, 2015, 6.8, 'The great media prankster, Joey Skaggs, wants to fool the world media AGAIN, and, with the most complex hoax of his career in the pipeline, he now must use every trick in his prankster''s arsenal to make it work. Art of the Prank is an emotional journey following the evolution of artist Joey Skaggs-a fierce proponent of independent thinking and the man who has turned the media hoax into an art form. With unprecedented access to the man and his archives, the 95-minute documentary interweaves a current unfolding hoax with a look behind-the-scenes at some classic performance pieces (all reported as fact by a wide range of prestigious journalists) plus commentary from co-conspirators and others. -IMDB', 'tt3267734', '/k4wX1ORltNYtO2IlMJMPa2qXt0w.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7745, 'Judas Kiss', False, True, False, False, 2011, 5.5, 'A quirk in time and space gives a failed filmmaker the chance to reshape his destiny when he visits his peculiar alma mater.', 'tt1242642', '/bEOKJFFFse35KbCQ0JuW4KXiHhb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7746, 'Lucky Numbers', False, True, False, False, 2000, 5, 'Russ Richards is a TV weatherman and local celebrity on the verge of losing his shirt. Desperate to escape financial ruin, he schemes with Crystal the TV station''s lotto ball girl to rig the state lottery drawing. The numbers come up right, but everything else goes wrong as the plan starts to unravel and the game turns rough.', 'tt0219952', '/Ak6R7cu4LAjEHf7L2p8iDBzPinV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7747, 'Tattoo Nation', False, True, False, False, 2013, 6.9, 'A documentary that follows three pioneers -- Charlie Cartwright, Jack Rudy and Freddy Negrete -- revolutionized the world of tattooing.', 'tt2207870', '/6tPae3tOWHS3pvcuhkvXEYmLfca.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7749, 'March or Die', False, True, False, False, 1977, 6.3, 'The French Foreign Legion, in the early 20s, is tasked to protect a group of Archaeologists in the middle east. After scenes depicting the hardship of day-to-day Foreign Legion life, and the ragtag collection of people who join, the local Arabs take offence at the Archaeologists and declare Jihad. A large battle takes place, with the inevitable last stand.', 'tt0076175', '/jW21NrRlcrA4kucmD9hfnPbQEvL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7752, 'K-11', False, True, False, False, 2013, 5.1, 'Raymond Saxx Jr., a powerful record producer, wakes from a drug-induced blackout to find himself locked up and classified "K-11." Plunged into a nightmarish world ruled by a transsexual diva named Mousey, Raymond is truly a fish out of water. Complicating matters are a troubled young transgender named Butterfly, a predatory child molester and the ruthless Sheriff''s Deputy, Lt. Johnson. Ray''s struggle to contact the outside world and regain his freedom seems impossible, but he must learn to navigate this new power structure if he is ever going survive and be in control of his life again.', 'tt1329177', '/ucQn8bEqCRstAHscjH4ryV08QgZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7754, 'Official Rejection', False, True, False, False, 2009, 7.2, 'A documentary following the exploits of a group of filmmakers as they take their independent feature, Ten ''til Noon, along the film festival circuit, and the politics, pitfalls, triumphs and comic tragedies they encounter along the way. Full of interviews with important players in the indie world, this is a must see for young filmmakers on the what happens when the shooting stops.', 'tt0780577', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7755, 'Superheroes', False, True, False, False, 2011, 6.5, 'A journey inside the world of real life caped crusaders. From all over America, these self-proclaimed crime fighters, don masks, homemade costumes and elaborate utility belts in an attempt to bring justice to evildoers everywhere.', 'tt1792621', '/eaMnt0Pe8nKj9DUV1eGBxBTs8sy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7756, 'In Tranzit', False, True, False, False, 2007, 5.8, 'Nazi POWs suspected of heinous acts are locked up in a Soviet women''s prison run by vengeful female guards. To weed out the guilty, the innocent must pay. Can supposed enemies turn into great loves? Based on a true post-World War II story, this drama stars Thomas Kretschmann, John Malkovich and Vera Farmiga in a bitter game of cat and mouse and a battle between hate and humanity, mercy and revenge.', 'tt0785012', '/dgdO3WvlTD9PfTQzoXIH9xYqBbD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7757, 'Love Me', False, True, False, False, 2014, 6.4, 'Love Me follows Western men and Ukrainian women as they embark on an unpredictable and riveting journey in search of love through the modern "mail-order bride" industry.', 'tt3249478', '/fOYQuFwqT09boJ4djJfpB4ZLrbe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7758, 'The Statement', False, True, False, False, 2003, 6.2, 'The film is set in France in the 1990s, the French were defeated by the Germans early in World War II, an armistice was signed in 1940 which effectively split France into a German occupied part in the North and a semi-independent part in the south which became known as Vichy France. In reality the Vichy government was a puppet regime controlled by the Germans. Part of the agreement was that the Vichy Government would assist with the ''cleansing'' of Jews from France. The Vichy government formed a police force called the Milice, who worked with the Germans...', 'tt0340376', '/tLwDdvsgQecx6SopQM9XEp8J1fY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7759, 'A Light Beneath Their Feet', False, True, False, False, 2016, 6.5, 'A high school senior must choose between enrolling at the college of her dreams and remaining at home to take care of her bipolar mother.', 'tt3262022', '/tIRo0HeKAaYIIXJjgEHzWbiel8y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7761, 'Shame', False, True, False, False, 1988, 7, 'Brandon is a New Yorker who shuns intimacy with women but feeds his desires with a compulsive addiction to sex. When his wayward younger sister moves into his apartment stirring memories of their shared painful past, Brandon''s insular life spirals out of control.', 'tt1723811', '/cAWLz9kFv4xc6IsEXTj2DrcqD55.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7765, 'Virtual Revolution', False, True, False, False, 2016, 4.9, 'Paris 2047. Most of the population spend all their time online, connected into virtual worlds, and don''t care anymore about reality. A shadow agent, Nash, working for one of the multinational companies behind these virtual worlds, is tracking down terrorists who threaten the system...', 'tt4054004', '/lxB6QUCLcmAgdYbSVhpdKYbTPQA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7767, 'Carpool', False, True, False, False, 1996, 5, 'A man with an important business meeting finds himself having to take care of the carpool for the neighborhood school children when his wife gets sick. Stopping to get donuts for the kids, things go even more awry when he finds himself a victim of a robbery. However, the situation only gets worse as a desperate man who had been contemplating a bank robbery robs the robbers and takes the man and the kids hostage in their van as his truck is blocked by an armored car. The thing then proceeds into a comedic chase movie. The father finds his kids don''t really respect him and they react better to the robber. The end result is everyone gets a lifestyle change, including the original store owner.', 'tt0115836', '/rYoZR4EtrfziT5WXhZzcNbH5GuE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7768, 'The Phantom Lover', False, True, False, False, 1995, 6.4, 'In 1936 China, a nearly bankrupt drama troupe starts performing in a burned-out theater where the great actor Song Danping was killed. One of the actors, Wei Qing, starts seeing strange apparitions that could revive his troupe and deliver him to the same fate as Song Danping.', 'tt0115016', '/wYBjIw5eXYTMBRU7zRpOAcbNNdK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7769, 'The Gambler', False, True, False, False, 2013, 7.2, 'Paramedic Vincentas is a passionate gambler, who is forced to make radical decisions to return the debts. An idea strikes Vincentas to create an illegal game related to his profession. Love, life and death will be at stake.', 'tt2924336', '/yTCeRcvWXmnpQyT3VUzHJY1CPuO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7771, 'A Pistol for Ringo', False, True, False, False, 1965, 6.5, 'Giuliano Gemma stars as a cool, cocky mercenary enlisted by a sheriff to infiltrate a ruthless gang of outlaws. Ennio Morricone provided the score to this early spaghetti western hit.', 'tt0059601', '/cxTIJRaY5FgogIddyJYdLy4mLjU.jpg');
