insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7773, 'Karla', False, True, False, False, 2006, 5.5, 'Based on real events, Canada''s most notorious serial killers, Paul Bernardo and wife Karla Homolka kidnap, sexually abuse, and murder three young girls.', 'tt0424938', '/mzKbHdw4pXs4QBsNE77LXEbJhZ7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7774, 'Beta Test', False, True, False, False, 2016, 5.9, 'While testing the latest first person shooter from global game developer, Sentinel, video game champion Max Troy discovers the events happening within the game are being reflected in the real world. He soon determines that the game''s protagonist is real-life Orson Creed, an ex-Sentinel employee who is being remotely controlled by the corporation for reasons unknown. As virtual and real worlds collide, Max and Creed must join forces to unravel the conspiracy before the game''s sinister events escalate and overwhelm the city.', 'tt4244162', '/zKisJfMxpp0KfX4P3VyDrH0edg6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7775, 'National Lampoon Presents Electric Apricot: Quest for Festeroo', False, True, False, False, 2007, 6.8, 'Electric Apricot is a spoof of jam bands centered around the band, Electric Apricot, in the style of "This Is Spinal Tap"', 'tt0482499', '/nLaLnBV3muilV3TwW5ddeBy3bCv.jpg');
