insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7825, 'Swastika', False, True, False, False, 1974, 7.4, 'Swastika is a feature length documentary about the way in which the Nazi regime infiltrated the lives of the German population, during 1933-45.', 'tt0070760', '/ahBZyN4ZM5IeqZdvtWVKHR0quXh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7826, 'The Bat', False, True, False, False, 1959, 6.1, 'Eisenstein gets in trouble for shooting a grouse. He is told that he must go to prison for his crime. However, his friend has invited him to an aristocratic ball. Eisenstein, despite being married, wants to go to the ball to meet women. Eisenstein lies to his wife. He tells her that he is going to prison but actually he goes to the ball. His story arises the suspicion of his wife. His wife devises a plot to catch her womanizing husband.', 'tt0079464', '/bktxrPnsIYt1QBdroAsJg8cpzr4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7828, 'Helicopter Mom', False, True, False, False, 2015, 5.5, 'Maggie Cooper thinks it would be really cool if her son Lloyd were gay. So cool, in fact, that she outs him to the entire school.', 'tt3094236', '/7ODRMt3nn5wvhIoaxeAC0kOvXIe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7829, 'The Brain That Wouldn''t Die', False, True, False, False, 1962, 4.4, 'Dr. Bill Cortner (Jason Evers) and his fiancée, Jan Compton (Virginia Leith), are driving to his lab when they get into a horrible car accident. Compton is decapitated. But Cortner is not fazed by this seemingly insurmountable hurdle. His expertise is in transplants, and he is excited to perform the first head transplant. Keeping Compton''s head alive in his lab, Cortner plans the groundbreaking yet unorthodox surgery. First, however, he needs a body.', 'tt0052646', '/kRqpDHw60W2bB6fMSwuTNI4uvsP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7831, 'An American Carol', False, True, False, False, 2008, 4.1, 'In An American Carol a cynical, Anti-American Hollywood filmmaker sets out on a crusade to abolish the 4th of July holiday. He is visited by three spirits who take him on a hilarious journey in an attempt to show him the true meaning of America.', 'tt1190617', '/uEDdzt1ZTB1BRo95gMkBytl0NqM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7832, 'Hunger', False, True, False, False, 2009, 5.3, 'A sensitive writer from a small town faces spiritual crisis as he tries to make it as a Hollywood screenwriter. Charlie Pontus (Joseph Culp) wanders around Los Angeles torn between his efforts to sell a screenplay and find his next meal. His natural optimism keeps him afloat as he walks the tight-rope between his love for the beautiful, exotic Ylayali (Kathleen Luong) and his desperate connection to The Chief (Robert Culp), the Hollywood producer who has the power to give life or take it away. Stubbornly refusing to relinquish his principles, he sinks deeper and deeper into spiritual crisis, finally confronting God in a Jobian showdown.', 'tt0237320', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7833, 'We Still Kill the Old Way', False, True, False, False, 2014, 6, 'A leftist professor wants the truth about two men killed during a hunting party; but the mafia, the Church and corrupt politicians don''t want him to learn it.', 'tt0061320', '/d5Uo46jkjs1HEogp4BIuY83QmaO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7834, 'Anastasia', False, True, False, False, 1997, 5.5, 'Russian exiles in Paris plot to collect ten million pounds from the Bank of England by grooming a destitute, suicidal girl to pose as heir to the Russian throne. While Bounin is coaching her he comes to believe she is really Anastasia. In the end the Empress must decide her claim.', 'tt0048947', '/26wHOXNt4XNn2lYT8i8pWx1JOP0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7836, 'An Afghan Love Story', False, True, False, False, 2013, 7.4, 'It’s snowing in Kabul, and gregarious waiter Mustafa charms a pretty student named Wajma. The pair begin a clandestine relationship—they’re playful and passionate but ever mindful of the societal rules they are breaking. After Wajma discovers she is pregnant, her certainty that Mustafa will marry her falters, and word of their dalliance gets out. Her father must decide between his culturally held right to uphold family honor and his devotion to his daughter.', 'tt2551492', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7837, 'Fortress', False, True, False, False, 2012, 5, 'When the commander of the crew of a B-17 Flying Fortress bomber is killed in action in a raid over Sicily in 1943, his replacement, a young, naive pilot struggles to be accepted by the plane''s already tight-knit Irish American crew.', 'tt1558575', '/oLFSVMmxWDpHzYxMlKP7yKO2ERQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7839, 'DeepStar Six', False, True, False, False, 1989, 5.3, 'The crew of an experimental underwater nuclear base are forced to struggle for their lives when their explorations disturb a creature who threatens to destroy their base.', 'tt0097179', '/lggoI5H7Dgmdz0Arfi07eOGxXVu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7843, 'The Farmer''s Wife', False, True, False, False, 1928, 5.9, 'As her surroundings are invaded by outsiders, THE FARMER''S WIFE stoically carries out her daily tasks in preparation for what will be her final day on her farm, the only land she has known. Unable to accept this forced future, subconscious memories return that ultimately lead her to connect more to the countryside of her youth than ever before.', 'tt2140519', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7844, 'Year of the Gun', False, True, False, False, 1991, 5.6, 'In this thriller, American novelist David Raybourne (Andrew McCarthy) accidentally becomes entangled in the Red Brigade''s terrorist plan to kidnap Italian Premier Aldo Moro during a research trip to Rome. As the terrorists attempt to kill David, he and his photojournalist friend (Sharon Stone) must struggle to stay alive.', 'tt0103303', '/bdL6xkhbWokFJIp6ENFF6qbtH8T.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7847, 'The Boy in the Plastic Bubble', False, True, False, False, 1976, 5.7, 'Based on a true story, Tod Lubitch is born with a deficient immune system (which is unlike being born with AIDS). As such, he must spend the rest of his life in a completely sterile environment. His room is completely hermetically sealed against bacteria and virus, his food is specially prepared, and his only human contact comes in the form of gloved hands. The movie follows his life into a teenager', 'tt0074236', '/vE0WftkcX1XqTFtHCZGxhWF0rM6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7848, 'Gamera', False, True, False, False, 1965, 5, 'A nuclear explosion in the far north unleashes Gamera, the legendary flying turtle, from his sleep under the ice. In his search for energy, Gamera wreaks havoc over the entire world, and it''s up to the scientists, assisted by a young boy with a strange sympathic link to the monster, to put a stop to Gamera''s rampage.', 'tt0059080', '/rBIY8J7polexRSKitg5AjoWr12G.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7850, 'Mirage Men', False, True, False, False, 2013, 6.4, 'UFOs: weapons of mass deception... For over 60 years teams within the US Air Force and Intelligence services exploited and manipulated beliefs about UFOs and ET visitations as part of their counterintelligence programmes. In doing so they spawned a mythology so powerful that it captivated and warped many brilliant minds, including several of their own. Now, for the first time, some of those behind these operations, and their victims, speak out, revealing a true story that is part Manchurian Candidate and part Close Encounters of the Third Kind.', 'tt2254010', '/iIJTQ9vSrHUkFdyKgGptds8tB1Z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7852, 'Muhammad: Legacy of a Prophet', False, True, False, False, 2002, 7.8, 'Tells the story of the seventh century prophet who changed world history in 23 years, and continues to shapes the lives of more than 1.2 billion people. The film takes viewers not only to ancient Middle Eastern sites where Muhammad''s story unfolds, but into the homes, mosques and workplaces of some of America''s estimated seven million Muslim to discover the many ways in which they follow Muhammad''s example.', 'tt0396743', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7857, 'The Last Summer of La Boyita', False, True, False, False, 2009, 7.1, 'Young Jorgelina feels estranged from her boy-crazy older sister, who has entered adolescence and doesn''t want to hang around with little kids anymore. Finding refuge in their Boyita camper-van, Jorgelina travels with her father to the countryside, where her lifelong playmate Mario is undergoing some unexpected changes of his own.', 'tt1404653', '/lSdyYVoIVihQ9xm1paMfaa9sqXN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7859, 'Wasting Away', False, True, False, False, 2007, 5.7, 'Brain freeze has never been so bad once you’ve tasted Ale Cream, as four friends inadvertently eat some radioactive ice-cream, turning them into zombies. Only problem is they don’t see themselves as the undead, but as super soldiers.', 'tt1027762', '/rlD4pa9QWxQJwIW6XDb6kvGPgtB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7863, 'Shiver', False, True, False, False, 2008, 6, 'Santi, a young high-school student with a serious physical reaction to sunlight, is forced by his health to move with his single mother to a shadowy, isolated village in the mountains of Spain where the inhabitants begin to reveal themselves as strangely xenophobic. When terrible, violent events begin to occur, Santi becomes first a pariah at school and then strongly suspected by the police of hideous murders. Santi himself, however, wonders if he is not the next victim.', 'tt0896927', '/mDwh6cUbDMN0jIBmyHpmMag1g5a.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7864, 'Deewaar: Let''s Bring Our Heroes Home', False, True, False, False, 2004, 6.1, 'Major Ranvir Kaul (Amitabh Bachchan) is a POW who was captured along with over 50 soldiers by Pakistani soldiers in 1971 during the war between India and Pakistan. 33 years later Ranvir''s son Gaurav (Akshaye Khanna) decides to go on a rescue mission to Pakistan and bring back his father who he has not seen since he was a child. Helping him on his mission is Khan (Sanjay Dutt) who had successfully escaped from the same prison. They steal, kill, and plan to get the prisoners out. This movie is inspired by the film The Great Escape.', 'tt0387989', '/An2v1gqXvtR4EAEhiGHhQ7lKwec.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7866, 'Yumeji', False, True, False, False, 1991, 7, 'Artist Yumeji has gained fame and recognition for his skills at painting as well as notoriety for his untamed lifestyle. Despite his betrothal to a beautiful and timid young woman of high birth, his libido turns to his many female models. Despite this freedom from constraint, his lust and artistic sentiment cause him nothing but an increasing awareness of the elusive embodiment of true Beauty. While traveling he encounters a mesmerizing widow who relentlessly searches for her husband''s body in the nearby lake, believing him killed at the hands of a ferocious roaming bandit. Infatuated with her beauty, he feigns to help her look for the corpse, only to unlock the mystery himself thereby sending him to further depths of debauchery and despair. This is the third and final film in director Suzuki Seijun''s critically acclaimed Taisho Trilogy.', 'tt0103316', '/bmECgC3VqWpbLRBKNRf9h5G77JB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7867, 'The Old Man and the Sea', False, True, False, False, 1990, 6.8, 'The Old Man and the Sea (Russian: Старик и море) is a 1999 paint-on-glass-animated short film directed by Aleksandr Petrov, based on the novel of the same name by Ernest Hemingway. The film won many awards, including the Academy Award for Animated Short Film. Work on the film took place in Montreal over a period of two and a half years and was funded by an assortment of Canadian, Russian and Japanese companies. French and English-language soundtracks to the film were released concurrently. It was the first animated film to be released in IMAX. Oscar winning animation of the classic Hemingway story.', 'tt0207639', '/ixULCtFsL5azArovcuvJfnPaNHb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7869, 'Poppy Shakespeare', False, True, False, False, 2008, 7.2, 'N has been a day patient at north London''s Dorothy Fish day hospital for 13 years - her ambition is never to leave. Then she meets glamourous new patient Poppy Shakespeare, an ad agency receptionist convinced she''s not mad.', 'tt1106448', '/obHSzH4snX3y35IOadeV6OprETY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7871, 'Special When Lit', False, True, False, False, 2009, 6.9, 'What made more money than the entire American movie industry through the 50s and 60s? Pinball. Special When Lit rediscovers the lure of a lost pop icon. A product of the mechanical and electrical age, the American invention swept the world and defined cool. Now it is relegated to a nostalgic footnote deserving a better fate. Joining the fans, collectors, designers and champion players from across the globe who share a world many of us didn''t know still existed.', 'tt1511532', '/fe6mqYAQF1DOqCj4lqTGANFdtI2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7872, 'Godspeed', False, True, False, False, 2016, 6.8, 'A Taiwanese drug mule hires an eccentric cab driver to take him down South so that he can deliver bags of heroin. Things don''t go quite to plan as the worst impulses of man plague everyone and everything, and the moments of brevity are so important among the torture and sadism.', 'tt5952306', '/qdBSaoHNvrkKOfBVqW8NbvxdzpV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7873, 'The Wrong Arm of the Law', False, True, False, False, 1963, 6.8, 'The crooks in London know how it works. No one carries guns and no one resists the police. Then a new gang appears that go one better. They dress as police and steal from the crooks. This upset''s the natural order of the police/criminal relationship and the police and the crooks join forces to catch the IPOs (Impersonating Police Officers), including an armoured car robbery in which the police must help the gangs to set a trap.', 'tt0056704', '/oxdMCFnToDRqPenYqw0kTBwCu5X.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7877, 'Harry Price: Ghost Hunter', False, True, False, False, 2015, 6.7, 'When MP''s wife Grace Goodwin is found naked on a London street, Harry Price is summoned to investigate claims that her house is haunted.', 'tt4974584', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7881, 'Lady Chatterley''s Lover', False, True, False, False, 1981, 5.2, 'A film adaptation of D.H. Lawrence''s novel. After a crippling injury leaves her husband impotent, Lady Chatterly is torn between her love for her husband and her physical desires. With her husband''s consent, she seeks out other means of fulfilling her needs.', 'tt0082640', '/3H58M1rUGO9AOve8GR0svRGmjau.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7885, '2 Nights Till Morning', False, True, False, False, 2015, 6.1, 'Caroline, a French architect in Vilnius, Lithuania, on business, has to spend an extra night in the city due to a delayed work meeting. At the bar of her hotel she meets Jaakko, a Finnish DJ who is in Vilnius for a gig. Caroline lets him understand that she doesn’t speak English, and Jaakko doesn’t speak French. Although they lack a common language, they find themselves enjoying each other’s company...', 'tt3828796', '/5YPT6nuOpr5foN4BoFmaBTERQG5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7887, 'Zebra Lounge', False, True, False, False, 2001, 4.8, 'A couple learns the dangers of a walk on the wild side in this erotic thriller. Alan and Wendy Barnet (Cameron Daddo and Brandy Ledford) have been married just long enough for the excitement to have gone out of their relationship, and they''re looking for a way to put some spice back in their lives. After placing a personal ad looking for another couple interested in swapping mates, the Barnets are led to the Zebra Lounge, where they meet Jack and Louise Bauer (Stephen Baldwin and Kristy  Swanson), a pair of seasoned erotic adventurers. While the Barnets find the swinging scene exciting at first, they soon decide it may be doing more harm than good to their marriage. But easing the Bauers out of their lives proves to be neither simple nor safe.', 'tt0271884', '/hyej1bAJkY39O1tVIYdjLM6nfl6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7888, 'The Greatest', False, True, False, False, 1977, 6, 'Teenagers Rose and Bennett were in love, and then a car crash claimed Bennett''s life. He left behind a grieving mother, father and younger brother, and Rose was left all alone. She has no family to turn to for support, so when she finds out she''s pregnant, she winds up at the Brewer''s door. She needs their help, and although they can''t quite admit it, they each need her so they can begin to heal.', 'tt1226232', '/51LL5Sw2f9PUuOusfdQ150ZKWJs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7889, 'Scrooge', False, True, False, False, 1935, 6.6, 'Scrooge, the ultimate Victorian miser, hasn''t a good word for Christmas, though his impoverished clerk Cratchit and nephew Fred are full of holiday spirit. But in the night, Scrooge is visited by spirits of another color. Straightforward adaptation of Dickens Written by Rod Crawford', 'tt0026972', '/n7LxIhzDZhTjJbUaZZVqaR21GKE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7892, 'Swan Lake', False, True, False, False, 1981, 7.8, 'The hero, Prince Siegfried, is out riding one day with his friends when he spies amidst swans on a lake a particularly eye-catching one with a crown on its head, the familiar Princess ...', 'tt0078349', '/yUw3JNVqGrgrRSNdx1PdrGk5NOH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7893, 'Happily Ever After', False, True, False, False, 2016, 5.3, 'The Wicked Queen is dead but her brother, Lord Maliss, seeks for revenge. Using the Magic Mirror to locate Snow White and the Prince, he transforms into a dragon and attacks. Maliss takes the Prince to the Realm of Doom. Snow White, with the aid of the Seven Dwarfesses, cousins of the Sevens Dwarves, must embark on a quest to save her true love.', 'tt0099733', '/kk37EGFyED0i81t1zbK3Zn1bHPy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7895, 'I Accuse', False, True, False, False, 2003, 5.6, 'After serving in the trenches of World War I, Jean Diaz recoils with such horror that he renounces love and personal pleasure to immerse himself in scientific research, seeking a machine to prevent war. He thinks he has succeeded, but the government subverts his discovery, and Europe slides with seeming inevitability toward World War II. In desperation, Diaz summons the ghosts of the war dead from the graves and fields of France to give silent, accusing protest.', 'tt0031503', '/tvqkFBbRbbY50iW7BcUIoUsyKWA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7896, 'The Hellbenders', False, True, False, False, 1967, 6.8, 'The Civil War has ended, but not for Jonas, a ruthless Confederate officer who wants to continue the fight by reorganizing Confederate troops in the Southwest with the support of a large sum of stolen money. He devises an elaborate ruse to allow his small party to travel with minimal scrutiny through hostile territory, for the money is hidden in a coffin said to contain the body of his dead son. Jonas'' other sons travel with him along with a hired "widow", as they proceed to what they hope to be a new start to the War between the States. However, while en route, they face severe, ongoing strife among themselves and successive threats from Union soldiers, a posse of cowboys, and an Indian war party.', 'tt0060261', '/vrqo3njFxG6Rvo1eEmz3cRZ0IGX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7898, 'Bride of the Monster', False, True, False, False, 1955, 4.2, 'Dr. Varnoff captures twelve men for his experiment: to turn them into supermen using atomic energy. Newspaperwoman Lawton gets too snoopy for her own good.', 'tt0047898', '/hB0xJnHUaZCENzaYauKRXRPo3gh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7899, 'Black Mountain Side', False, True, False, False, 2016, 5.2, 'A group of archaeologists uncover a strange structure in Northern Canada, dating over ten thousand years before the present. The team finds themselves isolated when their communication systems fail and it''s not long before they begin to feel the effects of the solitude.', 'tt3139756', '/g5emUKrTqhdn8QFtbEuxie4oOJT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7900, 'LolliLove', False, True, False, False, 2004, 6.2, 'A hip, misguided Southern California couple decide to make a difference in the lives of the homeless by giving them lollipops with a cheery slogan on the wrapper.', 'tt0316187', '/7GIE8Td9CG7x6uDnCdd3JDJsQ57.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7902, 'Don Q Son of Zorro', False, True, False, False, 1925, 7, 'Returning to the legend that inspired his first swashbuckling adventure, Fairbanks appeared in DON Q, SON OF ZORRO. Don Cesar De Vega (Fairbanks) crosses swords with a vicious member of the Queen''s Guard (Donald Crisp, who also directed), and steals the affection of a young heiress (Mary Astor). When the officer frames the young upstart for murder, Don Cesar fakes his own death and retreats to the crumbling ruins of the family castle he plots his vengeance.', 'tt0015758', '/epmYC0Tp9BUBoJsTRGkFj3W6hLq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7903, 'The Citizen', False, True, False, False, 2012, 7, 'The heart-wrenching story of The Citizen begins with a citizenship exam, where the examination committee rigorously questions a middle-aged African man. No matter how beautifully he recites Hungarian poetry, Wilson, a political refugee in his late fifties, fails the exams for the umpteenth time, because he doesn’t know where the periodical ‘Magyar Közlöny’ got its name from, and what the Corvinae are. Moreover, inspired by Vörösmarty’s poem, the committee chairman even questions his reasons for leaving his mother country. Wilson argues that his reasons include his fellow citizens cutting pregnant women in half, yet he doesn’t manage to soften the heart of the committee members.', 'tt4524678', '/eR7GuRgJVQU9RgKFqoftJ7bfuBz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7904, 'Cemetery Without Crosses', False, True, False, False, 1969, 6.9, 'Maria seeks revenge on the killers of her husband. She enlists the help of her husband''s best friend, Manual, a reluctant, but skilled, gunfighter.', 'tt0063740', '/mLh1miY6WwO9btwKoZlJVMgqpZV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7909, 'Girl on a Bicycle', False, True, False, False, 2013, 6.2, 'Paolo, an Italian tour bus driver living in Paris, has just summoned up the courage to propose marriage to his German girlfriend Greta. However, a chance encounter with a French woman on a bicycle the very next day turns Paolo''s life upside down.', 'tt1686042', '/89wfD5JffmKpxRWSwkdgoKiWWw3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7910, 'Daddy''s Little Girl', False, True, False, False, 2012, 5.8, 'After the police find Derek’s daughter brutally murdered on the beach, he vows to make the culprit suffer for his actions, even if that person is ‘part of the family.’  With careful research into the history of torture, Derek prepares himself and his basement for a week of brutal tactics that will make his daughter’s killer feel the pain that he has inflicted upon so many others.  In a time when stranger danger is ever present, a single father learns that it’s the ones you trust most who have to be watched the closest.', 'tt2214941', '/rohDI0xu1j7A10EFnoK8gVtJFMF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7913, 'Masked Avengers', False, True, False, False, 1981, 7.1, 'Philip Kwok plays a repentant killer who vows to destroy the masked gang of which he was a member. A young fighter and his martial arts brothers come to the town to catch the killers, but one of them is not to be trusted!', 'tt0082153', '/vr54E7RjvMLE4rFgb1NPgZdnpj6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7915, 'Refuge', False, True, False, False, 2013, 5, 'A post-apocalyptic thriller set in the ruins of a collapsed America, Refuge follows a family’s struggle to survive and rebuild a life in the absence of civilization. In the wake of a catastrophic plague, the family lives a lonely existence, struggling to maintain a sense of normalcy amidst a lawless world of roaming gangs and dwindling resources.', 'tt2292196', '/k989yAz1QairUqTQ9f2B9ZDsO9H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7917, 'Fools on the Hill', False, True, False, False, 2012, 8.2, 'A film about political ethics and legislative incompetence that follows one man''s attempts to require politicians to actually read the Bills they are passing -- which, strangely, is not currently required anywhere in the U.S.', 'tt1826692', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7918, '100 Days', False, True, False, False, 1991, 6.6, 'Devi is gifted with Extra Sensory Perception (ESP). Her gift comes to light one day while playing tennis with her friend, Sudha, when she has a vision of her sister, Rama, being shot to death by a man in a black raincoat. She telephones Rama, finds she is safe and is relieved. Shortly thereafter Rama disappears, the police are notified, but are unable to find any trace of her. Devi then meets multi-millionaire, Ram Kumar, both fall in love and get married. She moves into his once abandoned and legally disputed bungalow, and that''s when she starts having visions again. These visions lead her to a wall that is loosely plastered', 'tt0101244', '/pZJcxCrwjBl9bRLwOD8u5HfDT0x.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7920, 'Dus', False, True, False, False, 2005, 5.5, 'Siddhant Dheer lives a wealthy lifestyle with his brother, Shashank, and sister, Anu. Both Siddhant and Shashank work for India''s Anti-Terrorist Cell (ATC), along with Aditya and Aditi. After interrogating a suspect named Altaf, they get information that terrorists are planning a major strike in Canada that may endanger 25000 lives during the month of May. Altaf tells them the mastermind behind this attack is a terrorist named Jambhal, who has a contact named Himmat Mehndi. Shashank, and Aditya fly to Canada, meet with ATC Agent Neha, and then abduct Himmat, who denies that profusely. The duo team up with Canadian Police Officer Danish "Dan", and together attempt to piece together whatever information they have gathered from Himmat, only to find out that no one is who they claim they are, and the duo find themselves entrapped in a web of deceit and lies, with no clues as to who is behind the threat - the countdown for which has already begun. Written by rAjOo (gunwanti@hotmail.com)', 'tt0415768', '/vo3saMO3kDQe1gsA6lMBiALZcys.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7921, 'Repli-Kate', False, True, False, False, 2002, 5.7, 'A scientist and his sidekick accidentally clone a journalist and decide to turn the mistake into Repli-Kate, their idea of the perfect woman, who loves, beer, football, and frat parties.', 'tt0260320', '/947HeGfNJjiPYbzX90fDVJEMyoX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7924, 'Dirty Weekend', False, True, False, False, 2015, 4.9, 'A man and his mistress have just taken off for a weekend romp when they''re kidnapped by a trio of bank robbers. They wind up becoming media "stars" as police and reporters follow them. They all wind up at the beat-up shack of a cranky old codger, with the police surrounding them and the robbers threatening to kill everybody.', 'tt0068488', '/wnGcNuOSjBfRFhSbvG56HcOXFYc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7926, 'Black Water', False, True, False, False, 2018, 4.8, 'A terrifying tale of survival in the mangrove swamps of Northern Australia.', 'tt0816436', '/w4EhRFXNXctUtrFuUHIUh9j6Hb9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7928, 'Division III: Football''s Finest', False, True, False, False, 2011, 5.6, 'This ensemble comedy follows the Pullham University Bluecocks, a small liberal arts college with a Division III football program (the lowest division in the NCAA). When the head coach unexpectedly dies, the future of the flailing football program is in jeopardy, as they have not had a winning season in decades. In a desperate attempt to create some media attention for the athletic program and the university, President Georgia Anne Whistler hires known lunatic and felon, Coach Rick Vice, for what could be the football programs final season.', 'tt1562849', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7930, 'The Taiwan Oyster', False, True, False, False, 2012, 6.2, 'Taiwan in the year 2000, only thirteen years after the lifting of Marshall Law rule and still an unruly place referred to by many as the “Wild, Wild East,” provides a perfect setting for The Taiwan Oyster. Masquerading as a bender road-­‐movie, the darkly  comic film explores deep existentialist questions as the protagonists journey deeper and deeper into the Taiwanese countryside. Darin and Simon’s quest for the perfect burial spot becomes a strange trip down Taiwan’s picturesque East Coast Highway as they encounter the unique cultures of Taiwan, welcoming families, violent gangsters, and Nikita, another lost soul who joins their odyssey. Along the way, they explore the Southeast Asian ex-­patriot experience, their own personal demons, and the universal questions that arise when one is confronted  with an untimely death.', 'tt2115420', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7932, 'Food of Love', False, True, False, False, 2002, 6, 'Young aspiring pianist attracts attention of famous musicians. Chance encounters bring them together but expectations must be managed by all.', 'tt0309600', '/refAwGhC4I6CkAn2hNY69SIdYbn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7933, 'Happily N''Ever After', False, True, False, False, 2006, 4.5, 'An alliance of evil-doers, led by Frieda, looks to take over Fairy Tale Land. But when Ella realizes her stepmother is out to ruin her storybook existence, she takes a dramatic turn and blossoms into the leader of the resistance effort.', 'tt0308353', '/MjfHDN5WrV6RBVhCvfdlPpt7gH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7935, 'Black November', False, True, False, False, 2012, 5.3, 'A volatile, oil-rich Nigerian community wages war against their corrupt government and a multi-national oil corporation to protect their land from being destroyed by excessive drilling and spills.  To seek justice, a rebel organization kidnaps an American oil executive and demands that his corporation end the destruction and pollution.  Inspired by true events, Black November is the gripping story of how a community rises up and takes drastic measures to make sure their voices are heard.', 'tt2147225', '/onBy6iJt3BX8MSc7GjzIRgE07oV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7936, 'The Mystery of Chess Boxing', False, True, False, False, 1979, 6.9, 'Lee Yi Min stars as an eager young kung fu student who seeks to improve his fighting skills with an aim to avenge his fathers murder at the hands of the Ghost Face Killer an overwhelming force of destruction and master of the death dealing Five Element Fist. Lee eagerness to study attracts the attention of the master of Chess Boxing Jack Long who is the Ghost Face Killers arch enemy. Together master and student devise a wicked cross fertilization of the Chess Boxing and Five Element Fist styles and set out to put an end to the Ghost Faces deadly reign.', 'tt0199813', '/avYe0s8aE48PF5fy6v5oymryxf8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7937, 'Muthu', False, True, False, False, 1995, 7.5, 'Muthu starring by rajinikanth and meena. A lavish Maharaja (Rajinikanth) lived with his sister-in-law and stepbrother Rajasekhar (Raghuvaran), helping people incessantly. His sister-in-law has a son, to whom the Maharaja bequeaths a major portion of his property. At this juncture, a new baby is born to the Maharaja.His wife dies soon after. Brother Rajasekhar cheats the Maharaja as he fears that his son''s property might be taken back and given to the Maharaja''s own son. When the cheating comes to light, the Maharaja hands over all his property and his baby to his sister-in-law...', 'tt0140399', '/6tL4ujxvyY6Csxv5sKt0fDsCzf3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7939, 'Closure', False, True, False, False, 2013, 8.1, 'A documentary about a trans-racial adoptee who finds her birth mother, and meets the rest of a family who didn''t know she existed, including her birth father.  A story about identity, the complexities of trans-racial adoption, and most importantly, CLOSURE.', 'tt2405862', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7942, 'The Man Who Cheated Himself', False, True, False, False, 1950, 6.8, 'A veteran homicide detective who has witnessed his socialite girlfriend kill her husband sees his inexperienced brother assigned to the case.', 'tt0042707', '/jJKWfhHjDaFGSNvRnXx0Xxehhls.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7944, 'Remington and the Curse of the Zombadings', False, True, False, False, 2011, 5.6, 'Remington lives in a small provincial town. He’s a typical macho. During the day he works his ass off and at night he gets drunk to blow off steam. As a child Remington mercilessly harassed his homosexual neighbor until the man put a curse on him. One day Remington would turn gay. And it seems like the curse is slowly but surely working its magic. Remington is being forced out of a closet he never thought he’d be in. First his body language turns pink, then the way he talks and finally his sexual preference. Poor Remington finds himself entangled in a confusing threesome with his best friend and girlfriend. In the meantime his mother, a police officer, tries to find the perpetrator of a series of murders in the city’s gay and transsexual community. And the victims do not intend to remain under ground. They’re coming out of their coffins, Gloria Gaynor style !', 'tt1810861', '/tER1Ru1Grl5r8SIgcykoFztFoNZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7945, 'The Last of the Mohicans', False, True, False, False, 1920, 6.7, 'The story is set in the British province of New York during the French and Indian War, and concerns—in part—a Huron massacre (with passive French acquiescence) of between 500 to 1,500 Anglo-American troops, who had honorably surrendered at Fort William Henry, plus some women and servants; the kidnapping of two sisters, daughters of the British commander; and their rescue by the last Mohicans.', 'tt0027869', '/gT493jw0j2IPeFQFQVi5jLLhTs2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7947, 'Hollow Triumph', False, True, False, False, 1948, 6.8, 'A film noir drama in which a sophisticated con man, who has just stolen from the mob, is forced to impersonate a psychiatrist who looks just like him, going so far as to cut himself on the cheek to duplicate a similar scar on the doctor. When he takes matters even further, karma arrives, forcing him to pay the debts of his double.', 'tt0040444', '/doLTxo97Ga87TeLlRAfRu5HFkU9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7948, 'The Liberation of L.B. Jones', False, True, False, False, 1970, 6.8, 'A gloomy vision of the possibility of decent relations between whites and blacks anywhere, including the South. Undertaker L.B. Jones, the richest black man in his county of Tennessee, is divorcing his wife for infidelity with a white policeman. Taking a stand against racism, he is greeted with a hostile bunch of Southern bigots and other various stereotypes.', 'tt0065979', '/4FkuIbjdGkB5vzEdoC2fnUqySMV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7953, 'Flight from Death: The Quest for Immortality', False, True, False, False, 2003, 7.8, 'Narrated by Gabriel Byrne (Usual Suspects, Vanity Fair, Miller''s Crossing), this seven-time Best Documentary award-winning film (Silver Lake Film Festival, Beverly Hills Film Festival) is the most comprehensive and mind-blowing investigation of humankind''s relationship with death ever captured on film. Hailed by many viewers as a "life-transformational film," Flight from Death uncovers death anxiety as a possible root cause of many of our behaviors on a psychological, spiritual, and cultural level. Following the work of the late cultural anthropologist, Ernest Becker, and his Pulitzer Prize-winning book Denial of Death, this documentary explores the ongoing research of a group of social psychologists that may forever change the way we look at ourselves and the world. Over the last twenty-five years, this team of researchers has conducted over 300 laboratory studies, which substantiate Becker''s claim that death anxiety is a primary motivator of human behavior...', 'tt0365215', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7954, 'Minor Details', False, True, False, False, 2009, 4.5, 'Someone''s trying to make the students sick at the upscale boarding school, Danforth Academy! Abby, Paige, Claire and Taylor, join forces to solve the mystery. Whoever it is, the four best friends are going to find out!', 'tt1230126', '/c9xkBqj0YjmG4FtV7psJBz8ehcl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7955, 'Alyce Kills', False, True, False, False, 2011, 5.4, 'After accidentally knocking her best friend off a roof, Alyce is haunted by guilt and delves into a brutal nightmare wonderland of sex, drugs and violence, her mind tearing itself apart… along with anyone else who gets in her way.', 'tt1680051', '/fARw58q5YlgDFjD5ZdbiHIJ1Yh1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7956, 'Going Back', False, True, False, False, 2001, 5.2, 'A group of Marines return to Vietnam with a news crew to relive their tragic war experiences.', 'tt0243278', '/oNvyDciVvFVjPMQERPlpEJu0P0S.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7961, 'The Last Chance', False, True, False, False, 1945, 6.7, 'Escaping a Nazi prison train in war-torn Italy, an American and a British soldier set out for the Swiss border and find themselves leading a multi-national party of refugees for the Italian underground.', 'tt0037871', '/9F0ygov4xGFJzTngsazovWT8yPT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7964, 'The Daisy Chain', False, True, False, False, 2008, 5, 'A grieving couple move to a remote Irish village in the wake of their baby daughter''s death. They soon take in an orphaned autistic girl, only to become involved in a series of strange occurrences.', 'tt1129415', '/5MXgVuSfeGOHZjRb0cTqXclypXE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7966, 'The Zone', False, True, False, False, 2011, 5, 'Residents of an enclosed neighborhood in the middle of Mexico DF are shocked by a violent crime, and for one resident in particular, young Alejandro, the drama is ratcheted up when he encounters the lone kid who escaped the event and is hiding out within the neighborhood''s borders.', 'tt1039652', '/tAnUU7OB6QdnmYEd0R9veXVCXBr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7967, 'Amateur Night', False, True, False, False, 2016, 5.1, 'Guy Carter, an insecure expectant father unable to find work in his field, accepts a job driving hookers around Los Angeles. One long and crazy evening proves to our hero that he is, in fact, up to the task of fatherhood.', 'tt4265508', '/dzJFu1F0miZKHbR7tRIX6cQcI8c.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7969, 'The Fish Fall in Love', False, True, False, False, 2006, 6.9, 'After 22 years, Aziz decides to return to his hometown, to sell some family possessions, including the house where he was born. But for his astonishment, the place is now a small restaurant, run by four women, one of them a girl whom he once dated. Attieh, the girl, tries to convince him not to sell the place by, instead of telling him a story a day as Sheherazade, cooking him a delicious dish every day.', 'tt0877349', '/7WBf5xnplbaOrEcuQtIgqKy1JG4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7971, 'Vengeance', False, True, False, False, 1970, 7.1, 'A man tracks down the five outlaws who murdered his brother, all the while being shadowed by a mysterious Pinkerton detective.', 'tt0063159', '/fiYF9DLKoPA1MoKQQWALr4ePVZY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7976, 'Legend of the Eight Samurai', False, True, False, False, 1983, 6.5, 'The evil Hikita clan rises from the dead to fulfill a curse on the Satomi clan by restoring the face of their warlord by using the skin of Princess Shizu. In the process of trying to capture her, the clan murders her entire family, but Princess Shizu escapes their clutches. She enlists the aid of eight samurai warriors, possessors of powerful magic crystal, led by Dosetsu (Sonny Chiba!) to help rescue the remaining members of her court and revenge her family. Along the way, they must fight undead warriors, evil spirits, poisonous beauties and a giant centipede.', 'tt0086244', '/p2EAoeX730BJRxOMUulDYD0BEvn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7978, 'Diary of a Madman', False, True, False, False, 1963, 6.4, 'Simon Cordier, a French magistrate and amateur sculptor comes into contact with a malevolent entity. The invisible - yet corporeal - being, called a "horla" is capable of limited psychokinesis and complete mind control.', 'tt0056993', '/tIZTmFBG5kPEPABprFJFgeS9hqN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7979, 'Todd Barry: The Crowd Work Tour', False, True, False, False, 2014, 7.5, 'A Todd Barry show consists of two things: amazing jokes and amazing crowd work. In September 2013, he went on a tour without the amazing jokes and did entire shows of riffing and bantering with the audience. Filmed in seven west coast cities, “Todd Barry: The Crowd Work Tour” was directed by Lance Bangs and produced by Louis CK.', 'tt3639176', '/5L2uPJ05M0eAYHVFBaI9YHJC6Hv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7981, 'Comeback Season', False, True, False, False, 2006, 5.9, 'First Down. Second Chance. Skylar is a star quarterback of his football team. He''s got the skills, the looks, and the girls. His next-door neighbor, Walter (Liotta) is a loving husband, good father and all-round decent family man. When their worlds start to crumble they find themselves in jail together and suddenly these former enemies must rely on each other to rebuild their lives... finding wha', 'tt0455400', '/zSNW11igwXcCyX52RevUZMNGpv9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7983, 'Cold Sweat', False, True, False, False, 1970, 5.8, 'Mark Cahill is a good family man with a loving wife and two wonderful children. Mark also happens to be a hit man who is haunted by his last victim, a blonde, who keeps appearing in his bedroom at the worst of times. His life becomes even more complicated when he becomes caught up in the schemes of two business partners, one of the partner''s spouse and her boyfriend(s).', 'tt0106586', '/8ECiiSjjvfzMU7fKAiXMZgIiCax.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7986, 'Fear', False, True, False, False, 1990, 6.1, 'In Fear, documentary filmmaker Michiel van Erp creates a collage of inhabitants of the city of Amsterdam who struggle with various anxiety disorders. Today, more patients with anxiety disorders seek professional help than those who suffer from depression, making anxiety the number one mental illness in the Netherlands. This film will show how a small number of those patients attempt to overcome their fears, in order to get on with their lives in the crowded cosmopolitan city that Amsterdam is today.', 'tt1441846', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7987, 'Left Behind III: World at War', False, True, False, False, 2005, 4.6, 'A year and a half ago the world was hit with the biggest catastrophe it had ever seen. Without warning and without explanation, hundreds of millions of people simply vanished off the face of the earth. The world was in chaos like it had never been before. Yet somehow one man seemed to rise to the challenge. One man had the strength and conviction to unite a shattered world. One man gave the world hope. That man was NICOLAE CARPATHIA. He now rules the entire world.', 'tt0443567', '/neermOVb4v4eKCbFAGZt0wrKDKi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7989, 'Muffin Top: A Love Story', False, True, False, False, 2014, 4.7, '"Muffin Top: A Love Story" is the story of Suzanne (Cathryn Michon) a Women''s Studies Pop Culture professor at Malibu University, who studies images of women in the media for a living, and yet is made insecure by the constant parade of female perfection that is our airbrushed culture. She has been going through IVF treatments to get pregnant by her network executive husband (Diedrich Bader), but discovers on her birthday, that her husband has knocked up his younger, skinnier, co-worker (Haylie Duff) and wants a divorce. Happy Birthday! She goes on to find a more authentic version of who she really is, despite the delights of being suddenly single in Los Angeles, where low self-esteem for women is our number one export to the world.', 'tt2369600', '/sDMdEFvCZvUNTYZH9z3AYSwCzEC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7991, 'Movie Movie', False, True, False, False, 1978, 6.4, 'Three movie genres of the 1930s, boxing films, WWI aviation dramas, and backstage Broadway musicals, are satirized using the same cast.', 'tt0077952', '/ibbixbVqqAcs2fot7dUcyOALFsD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7992, 'The Chinese Boxer', False, True, False, False, 1970, 6.7, 'Lei Ming, a noble young martial arts student who doesn''t know the meaning of giving up. He faces a treacherous, blood-thirsty Japanese karate expert, which leads to many memorable battles as well as several unforgettable training sequences.', 'tt0065999', '/vDvvJSPI05A9dPSaFjoNd6IVsz7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7995, 'Capturing Mary', False, True, False, False, 2007, 6.9, 'A young man ushers an older woman into a dark exploration of her past - back to the time when, as a young girl, she met a stranger who affected her life forever.', 'tt0899052', '/eHc2KxSM98gQFdGXlfbPvrWG4Tq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7996, 'God''s Compass', False, True, False, False, 2016, 5.7, 'Suzanne Waters (Karen Abercrombie) has just passed the baton as the principal of the local high school. Having spent a lifetime teaching life lessons, the journey into retirement is about to bring her one of her greatest lessons. Her lesson plan will come from a troubled young man, Eli, as she invests in his life. Suzanne also becomes a first-time grandmother, and the new life of the baby brings renewed hope to the family as each member discovers their core value in God''s path for their lives. As Suzanne reflects back on her life of faithfulness, we are reminded that there are no accidents in God''s Kingdom, no chance meetings, no purposeless steps taken.', 'tt4623902', '/9GwdqmuZKDaly8lnzFkgzg40SqY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7997, 'Gacy', False, True, False, False, 2003, 4.7, 'Based on a true story of serial killer a model citizen, loving father and husband and serial killer John Wayne Gacy, a man with over 30 dead men and boys entombed in the crawl space underneath his house which he shared with his family.', 'tt0330181', '/jCEYOkub95lkFCfPbZJGhak1uZ0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8000, 'Malta Story', False, True, False, False, 1953, 6.5, 'In 1942 Britain was clinging to the island of Malta since it was critical to keeping Allied supply lines open. The Axis also wanted it for their own supply lines. Plenty of realistic reenactments and archival combat footage as the British are beseiged and try to fight off the Luftwaffe. Against this background, a RAF reconnaissance photographer''s romance with a local girl is endangered as he tries to plot enemy movements.', 'tt0046029', '/itkv9i8CvaBeKPJZwE4O4ORLFS2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8002, 'App', False, True, False, False, 2013, 5.5, 'A young psychology student is drawn into the dark and fearful world of a diabolic and mysterious App that starts to terrorize her, distributing compromising photographs, videos and text messages about herself and delves deeper and deeper into her personal life, flawlessly exposing all of her deepest secrets.', 'tt2536436', '/mlJTIFUbTBCqpOuLltLC8nlqkvL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8004, 'Summer of 8', False, True, False, False, 2016, 5.5, 'Eight close friends soak up their last day of summer together on the beach before parting ways for college.', 'tt4172146', '/ge2OYsxE1jmDcG4BgnA2Lh7BtYc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8005, 'Traveller', False, True, False, False, 1997, 6.1, 'A young man, Pat, visits the clan of gypsy-like grifters (Irish Travellers) in rural North Carolina from whom he is descended. He is at first rejected, but cousin Bokky takes him on as an apprentice. Pat learns the game while Bokky falls in love and desires a different life. Written by Jeff Hole', 'tt0120366', '/sgRravjx1m3guWa9otsizIbEJXx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8013, 'Sunny', False, True, False, False, 2008, 6.4, 'Na-mi, a transfer student, becomes a laughingstock on her first day of school because of her strong southern dialect. Six girls jump in to help the new kid adapt to the new environment, and the girls come to call their circle of friends "Sunny."', 'tt1937339', '/DnMVTIKg3GxTEXvZDYBxRtmwKv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8014, 'DeadHeads', False, True, False, False, 2011, 5.4, 'Two inexplicably coherent zombies awake amidst a zombie attack and decide to take a road trip to find the one''s lost love, unaware they are being chased by the agents of a ruthless company with it''s own agenda.', 'tt1273207', '/5c4wwRetiHFK7YUCoKpDTjvOz74.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8018, 'The Dust Factory', False, True, False, False, 2004, 6, 'Ryan is a teenager who lacks the ability to speak. One day, Ryan falls off a bridge and he finds himself transported to a strange fantasy world where he encounters his Grandpa Randolph (Armin Mueller-Stahl) and a pretty girl named Melanie (Hayden Panettiere); together, Ryan and Melanie learn to help one another with their problems, and they both discover the wisdom they can gain from elders like Grandpa Randolph.', 'tt0329030', '/lM7Tr8fAclJq5VB5k2kefyAPsbX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8019, 'The Kentuckian', False, True, False, False, 1955, 6.3, 'A frontiersman and his son fight to build a new home in Texas.', 'tt0048248', '/2mauUL3im15urcWHZkTdZGCKPsY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8020, 'The Silver Screen: Color Me Lavender', False, True, False, False, 1997, 6.3, 'A film scrapbook, images, phrases from our past, hiding their meanings behind veils. Let''s lift those veils, one by one, to find how images, at one time seeming innocent, have revealed, after decades, to have homosexual overtones.', 'tt0152444', '/s1TjxmFqj63rhvXq9zR4oo8UKXj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8023, 'The Dinosaur Project', False, True, False, False, 2012, 4.6, 'Found footage of an expedition into the Congo jungle where a team of explorers stumbles upon a colony of Dinosaurs.', 'tt1907779', '/o7CP9v5petfcIl6evwQ5Z0L62Qu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8027, 'Golden Swallow', False, True, False, False, 1968, 6.8, 'Golden Swallow revolves around the further adventures of its title character. This time around, she is forced into violence when a figure from her mysterious past goes on a killing rampage while leaving evidence that holds her responsible. Golden Swallow also makes room for a love triangle involving a mad, but righteous, swordsman named Silver Roc and a gentle warrior named Golden Whip. The three team up to conquer the evil forces of the martial world, but their joint venture only lasts so long, due to the two men''s egos. Ultimately, a duel to the death is planned between them, leaving Golden Swallow caught between two men, both of whom she admires.', 'tt0063105', '/2UBfO5fc9kLIlR7wFR4Ewb6tXOf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8028, 'Paradise', False, True, False, False, 2013, 5, 'Hanieh, a young teacher, lives in the household of her married sister in Tehran.  Increasingly exhausted by her paralysing everyday life and the two-faced reality she subliminally experiences as a woman, an employee, a younger sister, and unhappy partner, Hanieh struggles to find her way out.', 'tt4856674', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8029, 'Vile', False, True, False, False, 2011, 5, 'A group of attractive young people (of course) awaken to find themselves prisoners in a mysterious house with no means of escape. They are each outfitted with new accouterments--electronic devices attached to the base of their skulls--and a recording on a television screen alerts them of their task. In order to escape, they must hurt themselves and each other. The chemicals that the brain produces when in pain are collected by the devices, and when it collects a predetermined amount, the doors will be opened. Fail to do it in time, and everybody dies.', 'tt1711018', '/wHxVSKv4plCpllQpBt4tOftKJ12.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8030, 'The Piano in a Factory', False, True, False, False, 2011, 7.4, 'Steelworker Chen has a passion for music and plays the accordion in a local band with a close group of friends. When his estranged wife returns one day after years of absence, she demands a divorce and sole custody of their daughter. Chen is at a loss. He doesn''t mind divorcing a woman who has become a stranger, but he can''t bear to part with his daughter. Chen has worked hard to give her a respectable life and has taught her his love of music. When asked if she''d rather stay with her father or go with her mother, the girl gives a practical, devastating answer: she''ll go with whomever can provide her with a piano. Chen cannot afford such a luxury item, but the piano becomes his last hope to save what little is left of his family. With the help of his loyal friends and the support of his lover - the singer in his band - Chen concocts several plans to fulfill his daughter''s wish...', 'tt1723118', '/ss8B48cqPsuRSICvoxYYyT05QsH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8031, 'Lace Crater', False, True, False, False, 2015, 5.3, 'A woman encounters a strange presence in a guest house.', 'tt4792648', '/dQfXjgKYayelAJvZBo3r3we1Qjf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8032, 'The Horde', False, True, False, False, 2016, 4.2, 'A posse of crooked cops, malevolent gangsters and a horde of walking dead are the centre point of this gruesome, tight, action packed, claustrophobic tale of retribution and escape. Penned like animals, on the top floor of a deserted high-rise block, these two opposing gangs find that they are not alone in the lair of bloodthirsty corridors of death. Joining forces to survive, they must reach ground level together or perish. Loaded with a bad ass attitude, guns, axes and extremely creative hand-to-hand combat sequences that project the fears and paranoia that are the fighting forces behind the need to survive, when caught up in the wrong place at the wrong time.', 'tt1183276', '/c3sYdfCg1vz6DG4XlQRpu8baxvM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8037, 'Strip Search', False, True, False, False, 2004, 6.3, 'Strip Search follows several parallel stories examining personal freedoms vs. national security in the aftermath of 9/11; two main subplots involve an American woman detained in China and an Arab man detained in New York City.', 'tt0376215', '/2C27g52TOfhhuDGlsebkNKOyQY7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8039, 'Alice''s Adventures in Wonderland', False, True, False, False, 1972, 5.7, 'An all-star cast highlights this vibrant musical adaptation of Lewis Carroll''s immortal tale. One day, plucky young Alice follows a white rabbit down a hole and discovers a world of bizarre characters.', 'tt0068190', '/GaiN6EjIlY6wiPynV5XAG1mCrX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8041, 'Ace of Hearts', False, True, False, False, 2008, 5.6, 'Officer Daniel Harding is a distinguished member of the K-9 police unit. His partner and best friend is Ace, a police dog. When Ace is accused of mauling a suspect, an overzealous DA convinces police authorities that Ace should be euthanized.', 'tt1032746', '/maWhGQkqwT0cuPLsCmhJpuyoGht.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8043, 'Have a Good Funeral, My Friend... Sartana Will Pay', False, True, False, False, 1970, 6.8, 'After witnessing a brutal massacre, the legendary hero Sartana is ready to do some investigating. Almost everyone in the tiny town of Indian Creek seems eager to buy up the property left behind by the murder victims, and one of them could well be behind the killings. The sheriff himself is not above suspicion, so Sartana must uncover the culprit all on his own.', 'tt0065499', '/h4uQYbSlzKbS9joBck3d9lococr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8045, 'Madeline: Lost in Paris', False, True, False, False, 1999, 6, 'Madeline is the smallest of twelve girls in a boarding school, in an old house in Paris. When long lost Uncle Horst pays a surprise visit to Madeline, promising to move her to a new home in Vienna, her longing for a family seems to be fulfilled. Madeline becomes suspicious when her new Uncle loses his refined accent and takes an unexplained detour. Knowing she''s in trouble Madeline leaves a trail. It then turns out that uncle Horst is actually Henri, a miserable lackey who works for an even more miserable woman who runs a dingy factory. Ultimately Madeline helps the orphans who work there by notifying the outside world of their plight.', 'tt0272183', '/6fGOwtAz6O0TtwCvA5ZfWMLSp89.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8047, 'Running Wild', False, True, False, False, 2006, 6.3, 'A grissled cop with tendencies towards excessive force teams up with an ambitious and righteous prosecutor to track down a dangerous crime boss whom they both want desperately to catch. But their quarry resorts to devious methods to stall their investigation and eventually turn them against each other. Now the stage is set for a dangerous triangle.', 'tt0485552', '/73a6ePNaH4ISXch6zs3s94EJXjt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8048, 'Shock', False, True, False, False, 1977, 6.3, 'In this thriller, psychiatrist Dr. Cross (Vincent Price) kills his wife and expects to get away with murder, until he discovers that the slaying was observed by a next-door neighbor, Janet Stewart (Anabel Shaw). As Janet attempts to convince her husband (Frank Latimore) of the doctor''s dastardly deed, Cross shows up to advise him that Janet is in dire need of some in-depth counseling.', 'tt0038937', '/4rKGg9q6wL243k9qlqGLGtUSR6T.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8051, 'Crazy', False, True, False, False, 2008, 6.5, 'Benjamin is a 16 year old half-side spastic with lousy grades in math, who switches to a boarding school to reach grammar school. Acclimatization to the new environment is difficult in the first place and he has to struggle with the various problems of coming of age, when he falls in love to the school''s dream girl Malen.', 'tt0215681', '/9tZ36iWvmrzupMOvleGh57nb94Y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8054, 'Vendetta', False, True, False, False, 1999, 6.1, 'A hard-nosed detective deliberately commits a crime to get thrown in prison, allowing him the chance to seek vengeance on a criminal serving a life sentence for brutally murdering his wife.', 'tt3746298', '/7hfZbdtuxmbDbXZocYOVQNmUeXy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8055, 'The Trial of the Incredible Hulk', False, True, False, False, 1989, 5.9, 'On the run again, Dr. David Banner is jailed for assault after interrupting a mugging. Blind attorney Matt Murdock enlists Banner''s help in locating the muggers because he believes they work for his longtime foe, Fisk, the head of an international crime network. But David, afraid of public exposure, breaks out of jail as the Hulk. Tracking David down, Murdock reveals his own secret: His blindness came from a radioactive spill, and after developing his other senses so incredibly, he has become the amazingly athletic crime fighter called Daredevil. Fisk must now face off against Daredevil and the Incredible Hulk!', 'tt0098512', '/rwaH2I44q5jXJ2iMIuCenAUqZko.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8056, 'The Call of the Wild', False, True, False, False, 1972, 6, 'A prospector, a trapper and their sled dog battle the Klondike elements on their journey to get to the gold country.', 'tt0074270', '/kXDjSAWJrudIQ7ST6wfmsiMAiKo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8057, 'Columbus Day', False, True, False, False, 2008, 5.3, 'A thief tries to fix the damage done during the biggest heist of his career.', 'tt1015976', '/s7pRQP0ZdDFGPVUXYPPlmkZUNlw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8059, 'Tom Sawyer & Huckleberry Finn', False, True, False, False, 2014, 5.2, 'The adventure unfolds as Tom Sawyer and Huck Finn - Tom''s friend from the streets - witness a murder in the graveyard. Tom and Huck flee to Jackson Island and make a pact never to tell anyone about the incident. However, when the good-natured Muff Potter, who has been blamed for the murder is sentenced to death by hanging, Tom breaks his promise and returns to exonerate Muff Potter. In jun Joe, the actual murderer, makes a hasty exit from the courtroom during the trial. A short time later, Tom and Huck find references to a treasure and have to face In jun Joe again.', 'tt1977087', '/eRhKaIvIWKhEH60L9opF2aWdhrW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8060, 'Neverlake', False, True, False, False, 2014, 5.3, 'On a trip home to visit her father, Jenny is thrown into a world of mystery, horror and legend when she is called upon by 3000 year old spirits of the Neverlake to help return their lost artifacts and save the lives of missing children.', 'tt3301196', '/1geR9xZaNkwjE4gEdBdvzg8PC9i.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8062, 'Gary Gulman: It''s About Time', False, True, False, False, 2016, 7.2, 'Netflix presents standup and two-time Last Comic Standing finalist Gary Gulman. Titled It’s About Time, the tour marks Gulman’s 20th year in comedy and stops at the Bowery Ballroom in New York.', 'tt5661030', '/cCrBOXkFVV8aVyF73cMDpRnhJiu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8064, 'Thunderbird 6', False, True, False, False, 1968, 6.3, 'The International Rescue team is faced with one of its toughest challenges yet, as the revolutionary lighter-than-air craft Skyship One is hijacked while on her maiden voyage around the world. Against backdrops including the Statue of Liberty and the Sphinx, Lady Penelope, Parker, Alan and Tin-Tin fight the hijackers from on-board, while the rest of the team tries to stop the airship crashing.', 'tt0063694', '/foOzaiY4cfZLmWsd9XibK0UqXtu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8065, 'Ignition', False, True, False, False, 2002, 5.1, 'A web of intrigue and conspiracy lies beneath the euphoria as the U.S. is about to put a man on the moon for the first time in over thirty years', 'tt0247419', '/zaCeesx7ImJt6vFs6cA2AOreaL1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8066, 'The Blacksmith', False, True, False, False, 1922, 6.9, 'Buster clowns around in a blacksmith''s shop until he and the smithy get in a fight which sends the smithy to jail. Buster helps several customers with horses, then destroys a Rolls Royce while fixing the car parked next to it.', 'tt0012945', '/huJJfLX5X8ucItriHUKRVmgY4Fh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8067, 'I Love You Too', False, True, False, False, 2010, 6, 'Written by comedian Peter Helliar, I LOVE YOU TOO stars Brendan Cowell as Jim, a 30-something emotionally stunted man whose inability to declare his love to his girlfriend, Alice, threatens to cost him the best thing he ever had but leads him to befriend a talented dwarf who helps him find the words to get her back.', 'tt1376709', '/yhtLSMANn5GZqnzHjYYBkn8dEs9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8072, 'Corridors of Blood', False, True, False, False, 1958, 6.5, 'An 1840s British surgeon, experiments with anesthetic gases in an effort to make surgery pain-free. While doing so, his demonstration before a panel of his peers ends in a horrific mishap with his patient awakening under the knife; he is forced to leave his position in disgrace. To complicate matters, he becomes addicted to the gases and gets involved with a gang of criminals, led by Black Ben and his henchman Resurrection Joe.', 'tt0051542', '/iO1enF7KV8iYmIbwqWctiiV6i8B.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8075, 'Top of the Food Chain', False, True, False, False, 1999, 6.2, 'An isolated Canadian town (populated by the weirdest group of people this side of Saturn) has seen its share of problems. First the nut factory closed, then the CATV antenna stopped broadcasting, and now something is gruesomely devouring the townsfolk! Can visiting atomic scientist (and expert on "cool fusion") Dr. Karel Lamonte solve the mystery before everyone disappears?', 'tt0159797', '/wW99jyWrCRKgeUpTvf84P8BwxMB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8076, 'The Rainbow', False, True, False, False, 2019, 7.2, 'Ken Russell''s rather loose adaptation of the last part of D.H. Lawrence''s "The Rainbow" sees impulsive young Ursula coming of age in pastoral England around the time of the Boer War. At school, she is introduced to lovemaking by a bisexual physical education instructress. While experiencing disillusionment in her first career attempt (teaching), she has an affair with a young Army officer, who wants to marry her. Unable to accept a future of domesticity, she breaks with him, and eventually leaves home in search of her destiny.', 'tt0098165', '/iZ3iqYCVuNfIKWi56aYerDljYS9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8077, 'Shark Attack 3: Megalodon', False, True, False, False, 2002, 2.9, 'When two researchers discover a colossal shark''s tooth off the Mexican coast their worst fears surface - the most menacing beast to ever rule the waters is still alive and mercilessly feeding on anything that crosses its path. Now they must hunt the fierce killer and destroy it... before there is no one left to stop it', 'tt0313597', '/ldo8KxwOKujOE8t407h8RN1QF0c.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8082, 'Mantervention', False, True, False, False, 2014, 4.8, 'Spencer turns to his best friend who''s planned an unorthodox "mantervention" filled with debauchery to convert him from hopeless romantic to forever player, and in the process they discover that being in love is not so bad after all.', 'tt2318268', '/t9VltY8uGC1tyHohW34RXQAieAw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8084, 'The Image Revolution', False, True, False, False, 2013, 7.2, 'Twenty years ago, seven superstar artists left Marvel Comics to create their own company, Image Comics, a company that continues to influence mainstream comics and pop culture to this day. Image began as more than just a publisher - it was a response to years of creator mistreatment, and changed comics forever. The Image Revolution tells the story of Image Comics, from its founders'' work at Marvel, through Image''s early success, company difficulties during the comics market implosion, and ultimately the publisher''s new generation of properties like The Walking Dead. Filled with colorful characters, the film is a clarion call to artists to take control of their destiny.', 'tt2294916', '/cRjdWCsxBYDK1KWMmGKthPTtZOf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8088, 'Pink Floyd: Behind the Wall', False, True, False, False, 2011, 6.8, 'This documentary looks at the conception, design and live shows of The Wall performed by Pink Floyd in 1980 and 1981. It features in-depth 1980s era interviews with Roger Waters, David Gilmour, Richard Wright and Nick Mason and shows footage of The Wall performed at Earl''s Court in 1980. It also features archival footage of the Syd Barrett era Pink Floyd and discusses how David Gilmour was brought into the band to initially augment their live shows when Syd became unreliable due to his drug problem and how Gilmour ultimately replaced him.', 'tt1957996', '/gIDinNfjIJQuEc64MrQRafjADsL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8093, 'Bill Maher: Victory Begins at Home', False, True, False, False, 2003, 7, 'Maher addresses contemporary political, social and cultural topics -- Iraq, President Bush and the so called Axis of Evil. The opinionated Maher said about Victory Begins at Home: "We''ve heard everything about the War on Terrorism except what we can actually do to help win it. The government used to do that for us through propaganda (the positive kind) posters, so taking my cue from the great old posters of World War I and World War II (''Loose Lips Sink Ships,'' ''Buy War Bonds,'' ''Plant a Victory Garden,'' etc.) I commissioned artists to paint the posters our government today should be putting out to help us win this war."', 'tt0377515', '/2zyfd3NuIkB7P4Of1KEqtEoY3Kn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8095, 'Inseparable', False, True, False, False, 2011, 5.4, 'It''s April of 1986. Pripyat, Ukraine. A core meltdown occurred at Reactor 4 of the Chernobyl Power Plant. Over thirty people died during the blast but the estimated death toll as a result of the radiation fallout will reach a four-digit number. This accident will be considered the world’s largest ecological disaster.  Set at ground zero of a catastrophe, this film will reveal the details of the Chernobyl event. The shocking news of the explosion at the nuclear plant has spread, as the world watched the tragedy in fear and confusion. Hearts went out to those who displayed selfless courage and integrity in the initial efforts to stabilize the reactor power.  But some were too busy being in love to notice what had just happened in nearby Chernobyl. The disaster has faded into the background...  This is an account of love and loss, bravery and supernatural stoicism in the face of unbeatable odds.', 'tt2934916', '/rDjnjs3eUcSxd0Mj4YdBRnZ0gfo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8097, 'Baadshah', False, True, False, False, 2013, 6.4, 'K. Jhunjhunwala would like to locate his missing/runaway daughter, Seema Malhotra, so that he can get her married to Nitin Dharampal; Prominent businessman, Mahindra, would like to negotiate with his daughter, Sonu''s kidnappers, and secure her release. And the Central Bureau of Investigation would like to protect the life of Chief Minister Gayetri Bachchan under the mission code-named "Maa", and the CBI Officer in Charge to be code-named "Baadshah". Now Baadshah happens to be a bumbling and out of work Private Investigator, who has agreed to assist Jhunjhunwala and Mahindra, and while doing so is mistaken for the CBI Agent, ends up working for the assassins, and must assassinate Gayetri or end up losing his team of four fellow-bumblers, and Mahindra''s daughter. What results is hilarious chaos, with no one knowing what the other is doing, and each one being each other''s suspect(s). To make matters worse Jhunjhunwala is not really Seema''s dad, but someone else with an ulterior motive.', 'tt0211934', '/fXGeUIbEfgO3cfHuYuPkXnKVt43.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8098, 'The Benefactor', False, True, False, False, 2015, 5.1, 'A newly married couple are forced to navigate the all-consuming interest of a powerful, mysterious, and possessive philanthropist.', 'tt3165630', '/rvUazZEuj1UBK58TIxXEsla2Phy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8100, 'Evil Ed', False, True, False, False, 1995, 5.6, 'Edward is a friendly, harmless film cutter on the culture department. After an suicide accident, he is put on the mission to cut the "Loose limbs"-series. The blood, gore and violence makes him slowly goes insane. Edward slowly turns into Ed. Evil Ed ...', 'tt0116247', '/litNjFux0JLQLxYpZxQHiZkMFwh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8102, 'Eloise', False, True, False, False, 2017, 4.6, 'Four friends break into an abandoned insane asylum in search of a death certificate which will grant one of them a large inheritance. However, finding it soon becomes the least of their worries in a place haunted by dark memories.', 'tt3692652', '/7hXgwxOhaLwmyAE67Ky6ZOjyTdL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8104, 'The Return of Ringo', False, True, False, False, 1965, 6.8, 'Once again billed as Montgomery Wood, Giuliano Gemma plays a civil war soldier who returns to his family land to find his family decimated, his property taken over by a family of Mexican bandits and his fiancee about to marry the Mexican gangster behind all this. Bent on revenge, he goes undercover disguised as a Mexican and discovers he has a daughter!', 'tt0060903', '/eiC4rgNbOT1V4cOMzH5tE9NMDte.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8105, 'Double Dragon', False, True, False, False, 1994, 3.8, 'Set 15 years in the future in post-earthquake California, where San Diego and Los Angeles are merged into one city, two teenage brothers have half of a powerful ancient Chinese talisman. An evil gang leader named Bo Abobo has the other half, and determines to get the brothers'' half and have a complete medallion so he can gain absolute power of the magical Double Dragon medallion.', 'tt0106761', '/l7Yd9vQ5fdCls7hgthblvTcK2T3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8107, 'Neon Flesh', False, True, False, False, 2010, 5.7, 'Hoping to earn his mother''s respect, a young hustler-turned-businessman tries to open a brothel with his two useless buddies.', 'tt1259570', '/v9rktdg6PkkpL25cKO7DXblarXz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8112, 'Wake of Death', False, True, False, False, 2004, 5.7, 'Action superstar, Jean-Claude Van Damme (Universal Soldier, Street Fighter), is back and at his hard-hitting best as Ben Archer, an ex-mob enforcer seeking revenge against a ruthless Chinese kingpin responsible for his wife''s brutal murder. When Archer joins forces with his old underworld friends, an all-out war is waged against the Chinese Triad in this pulse-pounding action thriller.', 'tt0367478', '/vfDVpBus7BaUiT6UsjpiedyPtnX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8113, 'Dead Awake', False, True, False, False, 2017, 4.6, 'Kate Bowman (Jocelin Donahue) is an average social worker who is investigating in the mysterious deaths of people who died in their sleep. Shortly before their deaths, the victims all reported a supernatural force that appeared to them while they were suffering from sleep-paralysis. When Kate investigates further into the case, she opens herself up to the creature''s wrath, and soon finds herself, and her family suffering from an ancient evil.', 'tt3778010', '/apBhaUivEub9Ghx7sj7UqQCFYH4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8117, 'Fögi Is a Bastard', False, True, False, False, 1998, 6.5, 'A 15 year old boy (Beni) falls in love with Fögi, a singer in a Rock band. As Fögi seduces him, he is only willing to follow him where ever Fögi wants to. But Fögi is a drug addict and pulls Beni deeper and deeper into the hell of drug addiction.', 'tt0166604', '/3Xhva2p9REB9XgF1G4TQZQOKgcS.jpg');
