insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8124, 'Savage Messiah', False, True, False, False, 2001, 6.5, 'The film fictionalizes the real relationship between French sculptor Henri Gaudier and Polish writer Sophie Brzeska, twenty years his senior, who came to Paris, she says, for its “creative atmosphere.”', 'tt0069225', '/riTgqLgAeIn4Gz2XA7clxyCj0yh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8125, 'Countess Dracula', False, True, False, False, 1971, 6, 'Based on the story of Elisabeth Bathory, the 18th century Countess who indulged herself in an orgy of murder and vampirism before before being walled up in her room by the authorities. The ageing Countess Dracula discovers that the blood of young virgins has a restorative effect on her celebrated beauty. Years later, she becomes engaged to a handsome young Hussar and is forced to repeat vile atrocities with ever-increasing regularity to hold off old age.', 'tt0065580', '/6geUq5734YVVBxZ18U9HuZCIsWa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8126, 'Five Shaolin Masters', False, True, False, False, 1974, 6.7, 'Hu Te et al. escape the burning Shaolin temple after the Qing soldiers destroyed it in Shaolin Temple. The group of 5 decide to develop secret codes to identify fellow patriots, enlist those patriots and eventually meet up again to escape to the south away from the Qings, and also identify the traitor who sold out Shaolin temple. Ma Fu Yi (the traitor, played by Wang Lung Wei), joins the Qing top fighters to eliminate the rebels but is exposed by Ma Chao-Tsing who gets captured by Ma Fu Yi. Hu meets up with a group of Shaolin men secretly posing as bandits to rescue Ma as their leader is killed in the process, thus the bandits join the rest of the Shaolin patriots.', 'tt0072991', '/zqGPXKTZlhcTRJEp90ZI9UpusxA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8128, 'Final', False, True, False, False, 2001, 5.9, 'Waking up in a nearly empty room, Bill has strange recollections of his father''s death and a car crash, and occasional paranoid delusions. Ann, a psychologist, tries to help him make sense of it all.', 'tt0277705', '/ubpAhxDc7E42O6QaaBtNR10ZwWP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8130, 'The Standoff', False, True, False, False, 2016, 5.4, 'At Big Jim’s Auto Depot there’s only one rule. The last man – or woman – standing wins. Eight teens enter, one teen leaves. Actually, all eight teens leave, but only one leaves with the ride of their dreams. To reach that dream, they’re going through a nightmare. Keep your hand on the car. That sounds simple enough, but two days of sleep deprivation and fierce competition can make things awfully hard.', 'tt4549224', '/5FxwA8I8WJgx1eSfQiSR2ZDdRIA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8131, 'Island of Fire', False, True, False, False, 1990, 5.9, 'Someone in a prison run by a corrupt warden fakes the deaths of convicts to later use them as expendable assassins. A police officer is sent into the prison to gather evidence of the corruption.', 'tt0099812', '/l9MPPqbWGkIMJnp177Wk4AxGtx8.jpg');
