insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8294, 'Mumbai Pune Mumbai', False, True, False, False, 2010, 8, 'A girl from Mumbai who comes to Pune to meet a prospective groom, with the idea of rejecting him, ends up spending the day with a complete stranger.', 'tt1822266', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8297, 'One Eyed Girl', False, True, False, False, 2014, 6.5, 'After the death of his girlfriend, Travis, a thirty-something psychiatrist, struggles to keep it together. On the brink of a nervous breakdown he stumbles across a strange church run by a charismatic leader, Pastor Jay. In search of answers Travis is led deeper and deeper into the underworld of religious fanaticism, home to a Doomsday cult and a teenage girl named Grace.', 'tt2307002', '/wxscaVHrFxaKFhV21CTHaEKKLIq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8300, 'The Babymakers', False, True, False, False, 2012, 4.9, 'After he flunks a fertility test, a man realizes that the only way he can get his wife pregnant is by robbing a sperm bank to take back the last of the deposits he made there years earlier.', 'tt0835418', '/7E46D0i88DJ5PVgBPYwXJHysITt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8301, 'The Big Ask', False, True, False, False, 2014, 4.8, 'The Big Ask is a dark comedy about three couples who head to the desert to help their friend heal after the death of his mother. They would do anything for him - except for the one thing he wants.', 'tt2327541', '/7vL3y3X3f9xt1mpIsWwG3D2EOdK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8304, 'Speed & Angels', False, True, False, False, 2008, 7.3, 'Inspired by the passion of two young Navy officers, director Peyton Wilson captures Jay and Meagan as they pursue their childhood dream of becoming naval aviators flying the F-14 Tomcat. Shot in epic Hi Definition, the aerial footage and stunning cockpit photography provide a dramatic backdrop for the more universal story of what it’s like to fight for your dreams. From dogfights in the Nevada desert, to night landings on aircraft carriers in the Atlantic what begins as a story of realizing a childhood dream turns into a story of fighting for one''s life.', 'tt1112756', '/5gd43sxD0vAg9Yw11R4vEwRjXyf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8306, 'Threshold', False, True, False, False, 1981, 6.1, 'The celebrated heart surgeon Dr. Vrain supports the research of the offbeat scientist Aldo Gehring, who is inventing an artificial heart. Dr. Vrain performs the first artificial human heart transplant against the advice of the Ethics Committee.', 'tt0083197', '/rPsU7FtLgKnfNS7IdKmS5upa8AF.jpg');
