insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8310, 'The Prisoner of Zenda', False, True, False, False, 1922, 6.7, 'Anthony Hope''s classic tale gets a decidedly ''un-classic'' treatment at the hands of Peter Sellers. Following the story somewhat, friends of the new King Rudolph of Ruritania fear for his life, and switch him with a look-a-like London cabby. Throw in two(!) lovely blondes, treachery, and a battle for life and honour, and enjoy life at its zaniest.', 'tt0079753', '/7ltgqtFA5XTeEy3QBWmOp7tglkn.jpg');
