insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8314, 'Licks', False, True, False, False, 2013, 6.5, 'The story of a young man, D, as he returns to his Oakland neighborhood after serving two years in prison for a robbery gone wrong. In the days after his release, we are taken into D’s world while he navigates the harsh realities that plague his inner city community. His judgment is tested by the insidious influences around him, forcing D to decide what direction to take his life.', 'tt2464812', '/50U9ZfzkLlbhP645BJ6MJBYJqQl.jpg');
