insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8319, 'El Gringo', False, True, False, False, 2012, 5.4, 'A man crossing into Mexico with a satchel of $2,000,000 – and a bloody past – suddenly finds himself under attack in the sleepy town of El Fronteras.', 'tt1990216', '/yM2bXInHW0CQdRDgM5GiJjAV1QQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8321, 'What Ever Happened to Baby Jane?', False, True, False, False, 1991, 6.2, 'Two aging film actresses live as virtual recluses in an old Hollywood mansion. Jane Hudson, a successful child star, cares for her crippled sister Blanche, whose career in later years eclipsed that of Jane. Now the two live together, their relationship affected by simmering subconscious thoughts of mutual envy, hate and revenge.', 'tt0056687', '/iL3NPfhVvZNW4HPBToRuk3DlXdV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8322, 'Russell Brand - From Addiction to Recovery', False, True, False, False, 2012, 7, 'BBC 3 follows actor and comedian Russell Brand, as he campaigns for abstinence-based recovery programmes and the compassionate treatment of addiction as an illness rather than a crime', 'tt2626962', '/596pRFqZhGVd2uMkxVpsTZq3Uyh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8324, 'The Great Rupert', False, True, False, False, 1950, 6.6, 'A Christmas Wish is a heartwarming holiday classic about a family led by (Jimmy Durante) who is down on their luck at Christmas time.Shortly before Christmas, they move into an apartment where Rupert the squirrel lives in the attic rafters. Just as it seems that the holiday will come and go without so much as a Christmas tree, Rupert acts as the family''s guardian angel - not only saving Christmas, but changing their lives forever.', 'tt0042524', '/aJGIPdcB8JddD3gpCTEIN9j6K1O.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8328, 'Redd Inc.', False, True, False, False, 2012, 5.9, 'Six captive office workers are literally chained to their desks by a demented, escaped serial killer; former regional manager Thomas Reddmann (Redd). He assigns his ''human resources'' the impossible task of proving his innocence or suffering gruesome consequences.', 'tt1857842', '/nVjAiAPkGbWlA1JE16MKkFw6tjs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8329, 'Gung Ho!', False, True, False, False, 1943, 6.1, 'A true-life epic that revolves around an exclusive bataillon of the U.S. Marine Corps during World War II, "Carlson''s Raiders," whose assignment is to take control of a South Pacific island once possessed by the United States but now under Japanese command.', 'tt0035958', '/iAzoqCjtMfxzbCNLmwUS6vYUn2H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8332, 'Wild Horses', False, True, False, False, 2015, 4.8, 'Texas Ranger Samantha Payne reopens a 15-year-old missing person case, and uncovers evidence that suggests that the boy was likely murdered on a ranch belonging to wealthy family man, Scott Briggs. When Scott’s estranged son unexpectedly returns home during the investigation, Samantha becomes even more convinced that the Briggs family was involved, and will stop at nothing to discover the truth about the boy’s death - even putting her own life in jeopardy.', 'tt4442130', '/77iVwo0oBaHRk4sFOBcMCh71xlr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8337, 'The Lost Medallion: The Adventures of Billy Stone', False, True, False, False, 2013, 4.8, 'A man who stops into a foster home to drop off some donations soon tells the kids a story about two teenage friends who uncover a long-lost medallion that transports them back in time.', 'tt1390539', '/cydYuT7j4JAmvWxsMGWTLtkLp2z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8338, 'Naked Fear', False, True, False, False, 2007, 5.2, 'When Diana (Danielle De Luca) takes a job as a stripper in a sleepy New Mexico town, she''s unaware that her fellow dancers have been disappearing. The local sheriff (Joe Mantegna) is stumped, but Diana learns the truth when she becomes the next victim. Her abductor takes her into the desert, where he strips her and lets her go. Then, he begins to hunt Diana with a rifle through the wilderness as s', 'tt0943326', '/niJO1FhGO5rjBHJgrht0xAve1QU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8339, 'Sex and Breakfast', False, True, False, False, 2007, 4.8, 'Two young couples using anonymous sex as a catalyst to fix their troubled relationships must rethink what makes a connection work.', 'tt0837803', '/xLyBfUq2FCnB0V8MmRhf6C4KFeh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8340, 'Awaken', False, True, False, False, 2013, 6.3, 'Alex''s dreary life is reignited when he meets Rachel, the woman of his dreams. The only thing that stands in the way of their perfect, serendipitous relationship is that Rachel has recently passed away.', 'tt1894616', '/3i24OmA054ZfGv3kloo0pLOM5kP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8341, 'Swing High, Swing Low', False, True, False, False, 1937, 6.3, 'In Panama, Maggie King meets soldier Skid Johnson on his last day in the army and reluctantly agrees to a date to celebrate. The two become involved in a nightclub brawl which causes Maggie to miss her ship back to the States. Now stranded, she''s forced to move in with Skid and his pal Harry. She soon falls in love with Skid. Skid gets a job playing the trumpet at a local club and becomes a big success. Fame and fortune go to his head which eventually destroys his relationship Maggie and his career.', 'tt0029626', '/xUjZ64w3qgDbglajGroUkxLlbnN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8343, 'St George''s Day', False, True, False, False, 2012, 5.2, 'Infamous London gangster cousins, Micky Mannock and Ray Collishaw, are at the top of the food chain, when their world is turned upside down as they lose a shipment of the Russian Mafia''s cocaine in rough seas. Set in London, Amsterdam and Berlin, the story races across Europe at breakneck speed as Micky and Ray attempt to stay one step ahead of the Police. Can they pull off a daring diamond heist in time to put things right and retire to a "legitimate" way of life.', 'tt1329232', '/9LWENzqjdRrTvsf0E002ytdc96P.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8345, 'Be My Valentine', False, True, False, False, 2013, 5.6, 'Firefighter lieutenant Dan Farrell (William Baldwin) is a widower and single dad who isn’t looking for love. But a few weeks before Valentine’s Day, his team responds to a fire at a local florist and he can’t help but notice the pretty owner, Kate (Natalie Brown). When he struggles to buy flower arrangements for the stations annual Valentine’s Day Firefighter’s Ball, Dan enlists Kate’s expertise and soon the two are spending lots of time getting to know each other. Inspired by his son Tyler’s (Christian Martyn) budding romance with another firefighter’s daughter, Dan starts to open up his own heart and even asks Kate to be his date to the ball. Just when he couldn’t be happier, Dan is faced with an expected emergency call: Kate’s ex, Gavin (James Thomas), has returned to win her back with a marriage proposal. Will Dan suit up to fight for love, or let the flame with Kate burn out?', 'tt2288568', '/6w7eP1sSsaq30VZlpqR9r2vOdy1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8348, 'The Assassin Next Door', False, True, False, False, 2009, 5.8, 'In an old apartment building on the wrong side of the tracks, two women, unknown to each other, live across the hall on the second floor. Galia is an assassin involved against her will with the local sex-traffic mafia. All she wants is to reunite with her little daughter that she left back home in Russia. Eleanor is a grocery store cashier and a battered wife. She dreams of winning the lottery and running away from her abusive husband. Galia and Eleanor don''t know each other, but as neighbors they share two things: an adjoining wall and a strong need to plan their escape. As Galia disobeys her latest contract, a woman target, and Eleanor discovers that she''s pregnant, the two women decide to take action against their oppressors in a fight for survival and freedom.', 'tt1198153', '/EWG78UUowHBMOsDXvDtHol07N3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8352, 'Countdown', False, True, False, False, 2011, 6.5, 'There will be an explosion exactly in 48 hours in Moscow. The information was provided by an agent who was killed this very second. All departments of special ops work on it, but the only hope is for recently organized independent team. They are a hacker girl who once broke thru Pentagon servers, a former special ops officer who enjoys speed and adrenalin, psychologist, a lady whose beauty outshines her intelligence and her rank of major, and a blind field engineer whose sense of smell and intuition substitute for his eye sight... And the Chief who got them all together and learned how to control them. Only this team is able to figure it out WHO, and most importantly WHERE, by knowing just WHEN...', 'tt0814236', '/jk8WcYQREK3sNEQSvgGxtw3wDK7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8357, 'The Witness', False, True, False, False, 2015, 6, 'A brother''s journey to unravel the truth about the mythic death and little known life of Kitty Genovese, who was reportedly murdered in front of 38 witnesses and has become the face of urban apathy.', 'tt3568002', '/7XuMlSJxU18VZdArqADZf8FkM9i.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8358, 'I Am Santa Claus', False, True, False, False, 2014, 6.6, 'Everyone thinks of Santa Claus as a magical figure without flaws, but the men who keep his legend alive, real-bearded professional Santas, are nothing like the jolly ol'' Saint Nick we''ve all grown up knowing. In reality, the mall Santa in your cherished children''s photo has problems just like the rest of us. Even the jolliest of men fall victim to divorce, job loss, insecurity and even the occasional hangover. ''I Am Santa Claus'' is a documentary that follows the lives of five real-bearded professional Santa Clauses as they anticipate and prepare the coming holiday season while showing them for who they actually are flawed, flesh and blood men who feel an overbearing responsibility to protect the integrity of the spotless, untarnished reputation of the "Red Suit."', 'tt2526846', '/rZtXyfBYPds3mZCVNx334SDdX2j.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8362, 'The Ride', False, True, False, False, 1997, 6.5, 'Two guys bought a car and travelled through the country untill they met a lonely girl on the road. So they begin to travel together having so much fun. And there is another guy who has a better car and who is looking for the girl because he is her lover, he is her first one. What is going on inside of this girl? Nobody knows, until the end.', 'tt0110202', '/beNrijPQaLdDTSTvV233ijzRuVY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8363, 'Across the Tracks', False, True, False, False, 1991, 5.8, 'When Billy returns from reform school he has to attend a different high school at the other side of town. He tries to start with a clean slate but his old rival doesn''t make it easy on him and his buddy Louie tries to make him go astray again. His brother Joe, quite the opposite of Billy, is a good runner and determined to win a track scholarship. He suggests Billy to join his school''s track team, which pits the two brothers against each other.', 'tt0101268', '/pdtXZeVJ5QBrwJO7lXw5m2y0mKF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8364, 'The Other Boleyn Girl', False, True, False, False, 2003, 6.1, 'A sumptuous and sensual tale of intrigue, romance and betrayal set against the backdrop of a defining moment in European history: two beautiful sisters, Anne and Mary Boleyn, driven by their family''s blind ambition, compete for the love of the handsome and passionate King Henry VIII.', 'tt0467200', '/9zD3hXV24zJzYvSuakXZU8yMEY4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8366, 'The Drummer', False, True, False, False, 2007, 7, 'When Sid insults a powerful triad boss, his father sends him to Taiwan for safety. Bored and restless, he discovers a community of Zen drummers high in the hills, and joins up - a decision inspired by a pretty face that soon becomes a challenge.', 'tt0831386', '/p98BrdNHdh4OyD3bQuVSqc1nL0g.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8368, 'The Adventures of Tartu', False, True, False, False, 1943, 7, 'British Captain Terence Stevenson (Robert Donat) accepts an assignment even more dangerous than his everyday job of defusing unexploded bombs. Fluent in Romanian and German and having studied chemical engineering, he is parachuted into Romania to assume the identity of Captain Jan Tartu, a member of the fascist Iron Guard. He makes his way to Czechoslovakia to steal the formula of a new Nazi poison gas and sabotage the factory where it is being manufactured.', 'tt0035612', '/d6LXYJbbnT4mDpsurZe1XA68ewC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8370, 'Steve-O: Guilty as Charged', False, True, False, False, 2016, 6.1, 'Comedy special starring comedian, author and all-round lunatic Steve-O, who went from humble beginnings as a circus clown to hitting the big time in ''Jackass''. Expect the unexpected as he performs hilariously outrageous stunts and riotous tricks that hold nothing back.', 'tt5223514', '/wUPEWjAFya9mAZbp0ks3qJJS2Ew.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8371, 'Bad Seed', False, True, False, False, 2000, 5.5, 'A young man-about-Paris, cut off from his father''s money, falls in with a picaresque gang of car thieves.', 'tt0025480', '/zhXe40oQgfmTv5c1iTPR7xigYRe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8375, 'Under the Bed', False, True, False, False, 2012, 4.6, 'Two brothers team up to battle a creature under the bed, in what is being described as a "suburban nightmare" tale.', 'tt1934458', '/hb6yVc7ij3jQv69dLTIkyna2K7B.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8379, 'The Wanderers', False, True, False, False, 2013, 7.4, 'The streets of the Bronx are owned by 60’s youth gangs where the joy and pain of adolescence is lived. Philip Kaufman tells his take on the novel by Richard Price about the history of the Italian-American gang ‘The Wanderers.’', 'tt0080117', '/oedFtX9hZZWpD7L5wkD0WxSteY2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8383, 'Sister Street Fighter', False, True, False, False, 1974, 6.3, 'Lee Long is a martial-arts champion who the police use as an undercover agent to infiltrate a drug ring responsible for importing heroin from Japan to Hong Kong. When he is identified and imprisoned, the police pressure his sister, Tina Long, to help them locate and free him. She gets the help of Lee''s martial-arts school, including the powerful Sonny Kawasaka, for the inevitable battle-royale with the drug gang, which includes masters of many different ''schools'' of fighting.', 'tt0073714', '/3pDkPHMqSXA69jU4yX3Wt305X8V.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8386, 'Brad Williams: Fun Size', False, True, False, False, 2015, 8.2, 'Comedian Brad Williams'' standup topics include his experiences as a little person and how to please your woman in bed.', 'tt4552412', '/rYmWwVII6JMnJMxJXHyXWBrhqpH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8387, 'Conception', False, True, False, False, 2011, 5.4, 'Conception is a clever, romantic comedy that proves it takes more than sex to make a baby. From a couple fighting the odds of fertility to young teenagers losing their virginity, the film follows nine very different couples on the night they conceive, showing that sex can sometimes be more neurotic than erotic. The hilarious ensemble cast includes Emmy-winner Julie Bowen and Sarah Hyland (“Modern Family”), Connie Britton (“Friday Night Lights”), David Arquette (Never Been Kissed), Jason Mantzoukas (“The League”), and Alan Tudyk (Dodgeball).', 'tt1619277', '/jUBEmGU3f3ItMInVkCfdWE4NMJJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8391, 'The Prize', False, True, False, False, 2011, 6.6, 'Ceci, a seven year old girl, has to keep a huge secret, but she doesn''t completely understand what is the secret about. The life of her family depends on her silence. But what exactly must she keep silent about? Ceci and her mom live hidden from military repression in Argentina. Ceci asks herself: what must she say? What should she really believe and do in order to deserve the love of her mother and others?', 'tt1525346', '/b9I0UZDaksIieigdRaognEadsNV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8395, 'Seize the Day', False, True, False, False, 1986, 6, 'Tommy Wilhelm (Robin Williams) is a salesman. An honest, hard-working guy who has lost his job, his girlfriend, and left part of his sanity behind as he heads to New York to pick up the pieces of his life. He''s always been able to sell, but caught in a downward spiral, he must, in addition, face the father who never really understood him, while trying to balance his newly precarious existence.', 'tt0091920', '/8lw1k5uiqqWyRaZ47sxITKlgKJc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8399, 'Satan''s Little Helper', False, True, False, False, 2004, 5.3, 'A naïve young boy unknowingly becomes the pawn of a serial killer.', 'tt0380687', '/osugwBXcbXZ2IYmzR4rCkrJxiQa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8401, 'A Blade in the Dark', False, True, False, False, 1983, 6, 'A killer stalks a composer staying at a posh Tuscany villa while writing the score to a horror film which has an incriminating clue to the killer''s identity.', 'tt0085303', '/lyizA1S8vQ2RudbOt4RS3etO3I8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8405, 'Ash Wednesday', False, True, False, False, 2002, 5.9, 'Ash Wednesday is set in the Manhattan of the early 1980''s and is about a pair of Irish-American brothers who become embroiled in a conflict with the Irish Mob.', 'tt0280438', '/sKyxf1nGw0iCIjXzlq6lIsVqq0g.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8408, 'Montana', False, True, False, False, 1998, 6.3, 'Nikolai, a Russian hit-man escapes from his adversaries into the streets of Los Angeles. He is taken into the household of Lesley and her son Johnny. In exchange for refuge, Nikolai repairs the single mother''s decaying house and symbolically mends fences between everyday people of Russia and the U.S. When enemies get too close to his new home, Nikolai makes his escape to Montana.', 'tt1108855', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8412, 'Sunday Too Far Away', False, True, False, False, 1975, 7.1, 'Friday night he''s too tired. Saturday night too drunk.', 'tt0073765', '/8nTL4YDnU0AmKOayXpi6nulrf9U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8413, 'Margaret', False, True, False, False, 2009, 7.2, 'A detailed and compelling portrait of one of the most formidable characters in British politics as she faces her final days in power. The year is 1990 and Margaret Thatcher''s support within the government is wavering - her hold on the premiership hangs in the balance. Then, long-serving politician Sir Geoffrey Howe resigns over Thatcher''s attitude to Europe. His resignation speech sparks a chain of events that leads to the overthrow of Britain''s first woman prime minister. This modern dramatic tragedy illustrates the strengths and fatal flaws of this iconic woman more clearly than ever before and reveals how the very aspects of her character that helped her secure power are the ones that ensured her downfall. Drama starring Lindsay Duncan.', 'tt1230150', '/dguQTIPnPAf9f14Jy14XelsYhje.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8416, 'The Past Is a Grotesque Animal', False, True, False, False, 2014, 7.3, 'A personal, accessible look at an artist - Kevin Barnes, frontman of the endlessly versatile indie pop band of Montreal - whose pursuit to make transcendent music at all costs drives him to value art over human relationships. As he struggles with all of those around him, family and bandmates alike, he''s forced to reconsider the future of the band, begging the question - is this really worth it?', 'tt3072636', '/wdvK5u5rP0KGMR8uIu2eSv3Z1zQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8417, 'Formosa Betrayed', False, True, False, False, 2010, 6.1, 'In the early 1980s, an FBI Agent is assigned to investigate the murder of a respected professor. Through his investigation, he unearths a spider web of international secrets that has been thriving within college campuses across America for decades. His investigation takes him across the Pacific to the island nation of Taiwan, where with the help of the outspoken widow and an unlikely spy, he learns that the Professor''s killing was not a random act, but a desperate move by a scandalous government intent on keeping its nefarious activities under wraps. Our detective soon finds himself on a collision course against the U.S. State Department, the Chinese Mafia, and the Nationalist Chinese Government - in a land where the truth is not what it seems and the only people he can trust, cannot be trusted at all. Inspired by actual events.', 'tt1121786', '/yN0qqQwoeTiTudll2Q1AVPM9qrT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8419, 'Return of the Street Fighter', False, True, False, False, 1974, 6.4, 'Second in a series starting with The Street Fighter starring Sonny Chiba. In this sequel, Martial artist Takuma Tsurugi returns to take on a Yakuza family that may be embezzling money from charities to finance their own operations. Both the police and the Yakuza find themselves battling Tsurugi, but Tsurugi''s fight ultimately is with the mob, and he concentrates on them.', 'tt0073623', '/3Rt1ekXzipjzUfVac8XRLnvRDhh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8420, 'The Way West', False, True, False, False, 1967, 6.2, 'In the mid-19th century, Senator William J. Tadlock leads a group of settlers overland in a quest to start a new settlement in the Western US. Tadlock is a highly principled and demanding taskmaster who is as hard on himself as he is on those who have joined his wagon train. He clashes with one of the new settlers, Lije Evans, who doesn''t quite appreciate Tadlock''s ways. Along the way, the families must face death and heartbreak and a sampling of frontier justice when one of them accidentally kills a young Indian boy.', 'tt0062479', '/l9Qi0XJ5HwoZh6ZbEyFaRW7l4An.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8422, 'Crazy Kind of Love', False, True, False, False, 2013, 5.8, 'A broken family finds their relationships to one another changed by a new arrival in the household.', 'tt1946310', '/t2cm8kBBlkKxUtVvq1f8q8uXdF0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8424, 'Intern Academy', False, True, False, False, 2004, 5.5, 'Follows the misadventures of a group of young interns at a hospital/medical school - dealing with the pressures of school and love.', 'tt0367232', '/fbmaUvSjC81ngZ5BkbxAc6p8U3I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8426, 'Love Hurts', False, True, False, False, 2009, 5.6, 'After his wife leaves him, middle-aged Ben Bingham slips into a funk and refuses to change out of his pajamas. Justin, Ben''s 17-year-old son, has the cure for what ails his depressed dad: he gives Ben a makeover and pushes him out into the singles scene. Soon Ben is the most popular guy in town, but when Justin falls in love for the first time, Ben must refocus his priorities and set about trying to win back his wife.', 'tt1213825', '/2YKxwZMbbgwjvf9cDPgtFwyOdnJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8428, 'Slumber Party Massacre II', False, True, False, False, 1987, 4.6, 'Courtney, the sister of a survivor from the 1st movie and the members of her rock band go to a condo for the weekend to play music and have fun with their boyfriends. Courtney''s dreams are of her sister, who is in a mental institution, warning her of having sex, and the dreams begin to spill into real life, threatening Courtney and her friends as they begin experiencing an attrition problem.', 'tt0093996', '/n1j7gCi3g2puzqyn7A2rPntHqjx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8429, 'The Nest', False, True, False, False, 1988, 5.3, 'Laborie is a high-flying officer in the French special forces. Her mission is to escort Abedin Nexhep, a godfather of the Albanian mafia. Charged with heading a wide-reaching prostitution network, this formidable criminal is due to stand trial before a European court. During the transfer, killers hired by Nexhep set up an ambush to free their boss but Laborie and her men manage to escape...', 'tt0280990', '/5mamCUxm8jaFVeMH7DFgrSticd5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8430, 'Miles', False, True, False, False, 2017, 5.8, 'High school senior Miles Walton joins the girls volleyball team in hopes of winning a college scholarship, which causes an uproar in his small Illinois farming town.', 'tt4066836', '/yUgGtvdEkwpCHf4LiVKZOh9UtUm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8431, 'The Divorce of Lady X', False, True, False, False, 1938, 6.7, 'The morning after a London barrister lets a mystery woman stay in his suite, a friend files for divorce.', 'tt0030063', '/m6UTujENdszPcInmOG07qeOzrSF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8432, 'The Killer Shrews', False, True, False, False, 1959, 4, 'Trapped on a remote island by a hurricane, a group discover a doctor has been experimenting on creating half sized humans. Unfortunately, his experiments have also created giant shrews, who when they have run out of small animals to eat, turn on the humans....', 'tt0052969', '/8vrO2aiCcAyQmY8wrqyusdWPTsR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8433, 'Recoil', False, True, False, False, 2011, 5.2, 'A cop turns vigilante after his family is murdered, exacting vengeance on the killers - and then on all criminals who have slipped through the system.', 'tt1839591', '/8myInTu5VyNXvvLyKpjoboXoCmn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8435, 'Forget Me Not', False, True, False, False, 2009, 5.2, 'The film follows Will Fletcher, a musician, and Eve Fisher, who works in a pub where he is performing, during one night in London. After Will has saved Eve from a drunken customer at closing time, they stay up all night together, meandering through the streets of London and forging a relationship. Next morning, Eve takes him to see her Alzheimer''s-suffering grandmother.  The film is often compared to Richard Linklater''s films "Before Sunrise" and "Before Sunset", as the style is very similar.', 'tt1431065', '/uhu2hFUvyECgOSnrwIv1PeS39E8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8438, 'Road North', False, True, False, False, 2012, 6.4, 'A sly riff on the Prodigal Son story, Mika Kaurismaki''s latest — about a joyless workaholic concert pianist who ends up on a wild ride with his long-lost rapscallion father — is a funny and cogent analysis of machismo, abandonment issues and the value of reconnecting with one''s roots. (TIFF)', 'tt1864557', '/nyuGf79xRhk3lgQ0v6lrAmRUywg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8445, 'Saturday the 14th', False, True, False, False, 1981, 4.7, 'Primarily a spoof of the Friday the 13th series, but also takes shots at several other horror films. After his family moves to a new house, a young boy discovers a mysterious book describing the curse hanging over the date of Saturday the 14th. Opening the book releases a band of monsters into the house, and the family must join together to save themselves and their neighborhood', 'tt0083033', '/tET9d7uIUKBH1RBMebVZkqEBZlR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8446, 'The Bet', False, True, False, False, 2020, 3, 'Two rival "brothers-in-law" make a bet that they can stop smoking for 2 weeks. But, it''s just not that easy...', 'tt0119863', '/53swr5MbRt8871pS2ugU4ZoC1gq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8449, 'Screwballs', False, True, False, False, 1983, 4.7, 'Welcome to T &amp; A High, where the entire student body including campus virgin Purity Busch, chronic masturbator Melvin Jerkovski, repressed Principal Stuckoff and blonde bombshell Bootsie Goodhead all must rise to the challenges of freshman breast exams, bikini cheerleader attacks, inappropriate uses of bowling ball holes and much more.', 'tt0086264', '/2oXSlVQBfPzyWtbvM9LC7AGfyoL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8450, 'Pete''s Christmas', False, True, False, False, 2013, 6, 'An overlooked middle child finds himself in the unexpected spotlight when he realizes his family''s terrible Christmas day keeps repeating. As the only one experiencing the day over and over, he decides to use his unique gift to give the holidays a makeover and his family a Christmas they will never forget.', 'tt3004544', '/kUThHZFYM6mEH3REIQ76BGMdkAi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8451, 'Plot of Fear', False, True, False, False, 1976, 6.1, 'Plot of Fear tells the story of a decadent weekend party full of orgies and drugs on the outskirts of Milan. After two deaths occur Inspector Lomenzo (Michele Placido) interrogates one of the guests, a fashion model (Corninne Clery) who becomes his informant, as well as his lover. Clery reveals that after a “wildlife orgy”one of the hosts tried to jokingly feed one of the prostitutes to a tiger but she got so frightened that she died of heart attack. In his attempt to find a connection between the victims, he investigates a cutting-edge security and surveillance firm whose director (Eli Wallach) has secrets of his own to hide.', 'tt0074451', '/cAFrA22iXVrCGy0IffbC3SCUNI0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8458, 'The Dirt Bike Kid', False, True, False, False, 1985, 5.6, 'When his mother sends Jack off with money to buy groceries, he comes home with a magic supercharged dirt bike instead. His mother is furious, but when Jack uses the magic bike to save the local hot dog stand from the clutches of corrupt big business, he becomes the town hero. Written by Concorde - New Horizons (with permission).', 'tt0090948', '/eJ6ciRPyZrAMjlEoJDbSZjw5uxB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8460, 'Escape to Athena', False, True, False, False, 1979, 5.7, 'During the World War II, the prisoners of a German camp in a greek island are trying to escape. They don''t want only their freedom, but they also seek for an ineffable treasure hidden in a monastery at the top of the island''s mountain.', 'tt0079117', '/e3ThvXgcnJ5FiJ8oALyrhTn0eVZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8461, 'Elephants Dream', False, True, False, False, 2006, 5.7, 'Emo and Proog are on a journey in the folds of a giant Machine, exploring the twisted and dark complex of wires, gears and cogs. Until one moment a conflict arises that throws out all their assumptions. This is the world''s first open source movie, created using the FOSS program Blender.', 'tt0807840', '/9zROtU9TkpZQrOuEaMAp68FOWLK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8462, 'Madagascar Skin', False, True, False, False, 1995, 6.3, 'This is the story of Harry and Flint, an unlikely couple. Harry is a shy young gay man who can''t seem to fit into his local bar scene. Flint is a crusty, older, and seemingly straight man with a questionable background. They meet on a gorgeous coastline, and evolve from distrust to deep love.', 'tt0113730', '/wkoUQG4BOqn94KCk3cHfoHDjp7l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8464, 'Last Summer in the Hamptons', False, True, False, False, 1995, 5.6, 'Filmed entirely on location in East Hampton, Long Island, "Last Summer in the Hamptons" concerns a large theatrical family spending the last weekend of their summer together at the decades-old family retreat which economic circumstances have forced them to put on the market. Victoria Foyt plays a young Hollywood actress whose visit wreaks havoc on the stellar group of family and friends - led by matriarch Viveca Lindfors and made up of an extraordinary mix of prominent New York actors, directors, and playwrights. In the course of a very unusual weekend, comic as well as serious situations arise, and the family''s secrets - of which there are many - begin to unravel.', 'tt0113612', '/cvKNOCgsXJGexLRh41H814oqszG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8467, 'The Perfect Wedding', False, True, False, False, 2012, 6.2, 'Christmas is approaching, and Paul''s adopted sister Alana is coming home for the holidays. This won''t be just a regular family celebration, as she''s recently engaged and the weekend will be spent planning her June wedding with her best friends Roy and Vicki. Problem is that Roy is Paul''s ex-boyfriend, and the two young men haven''t seen each other since their very messy breakup. Nervous about seeing his ex again, Roy talks his good friend Gavin into joining him for the weekend, and pretending to be his new boyfriend. When Paul and Gavin meet, sparks of attraction fly, but Gavin thinks Roy''s still hung up on Paul. This charming romantic comedy is a funny look at family bonds, misunderstandings and the "perfect wedding', 'tt2151863', '/1B7DOdbUONcKNHvTp4su3hi3VwW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8469, 'Population 436', False, True, False, False, 2006, 5.7, 'A census-taker (Sisto) is sent to investigate why a certain small town has had the same population -- 436 residents -- for the last 100 years.', 'tt0462482', '/xEbq0QEcqw7DyPYPsP2yX9PucpC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8470, 'Everybody Street', False, True, False, False, 2013, 7.7, 'Celebrated filmmaker and photographer Cheryl Dunn turns her lens on the pioneers and masters of New York street photography. Dunn profiles artists spanning six decades, including Bruce Davidson, Mary Ellen Mark, Jill Freedman, Jeff Mermelstein and Martha Cooper, revealing that these shooters are as colourful and unique as the subjects they’ve relentlessly documented. Everybody Street explores the passion that compelled Freedman to spend years riding in squad cars during the most violent years in the city; Bruce Gilden’s drive to thrust his camera in people’s faces to capture a moment; and Martha Cooper’s dedication to chasing graffiti on passing subway cars in the Bronx. The film is a definitive look at the iconic visionaries of this often imitated art form.', 'tt2784462', '/voAWjPZD62aCrDcABh3GGDvSN31.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8472, 'Beaver Trilogy Part IV', False, True, False, False, 2015, 7.4, 'A chance meeting in a parking lot in 1979 between filmmaker Trent Harris and a young man from Beaver, Utah inspired the creation of an underground film that is now known as Beaver Trilogy. But the film itself is only part of the story.', 'tt4264834', '/ekD5kIaXSVbIOzJ2i6aGYnm338d.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8473, 'Angela', False, True, False, False, 2002, 6.1, 'A ten year old girl named Angela leads her six year old sister, Ellie, through various regimens of ''purification'' in an attempt to rid themselves of their evil, which she believes to be the cause of their mother''s mental illness. Precocious, to say the least, Angela has visions of Lucifer coming to take her and her sister away, and one of her remedies for this is for them to remain within a circle of their dolls and toys until they see a vision of the virgin Mary come to them. But such thinking can only lead to an ending befitting of her own mental state.', 'tt0112364', '/1UxqC7TS1o1hzaPkc1Q0LsW0yUS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8474, 'The Golden Age of Comedy', False, True, False, False, 1957, 7.2, 'A compilation featuring comedic stars of the silent era including Laurel and Hardy, Buster Keaton, W.C. Fields and Harold Lloyd.', 'tt0129119', '/vvBi7y9Cp8dcqECKWLYTA0MjKg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8475, 'Shouting Fire: Stories from the Edge of Free Speech', False, True, False, False, 2009, 7.5, 'A documentary look at the changing interpretations of the first amendment of the U.S. Constitution - laws and court cases that have alternatively broadened and narrowed the amendment''s protection of free speech and assembly. The film''s thesis is that post-9/11 the government has seized unprecedented license to surveil, intimidate, arrest, and detain citizens and foreigners alike. The film also looks back to the Pentagon Papers'' case and compares it to cases since 9/11 dealing with high school students'' speech and protesters marching in New York City during the 2004 Republican convention. Comment comes from a range of scholars, pundits, and advocates.', 'tt1333657', '/nyHCUr2ITfzS26Q7WZvaoLNEi6e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8478, 'Green Ice', False, True, False, False, 1981, 5.6, 'He wanted adventure...She craved revenge...Emeralds held the answer.', 'tt0082476', '/7rm0GQfEr5N18o698HU9kwUdyV6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8479, 'Border Café', False, True, False, False, 2005, 7.3, 'A young widow takes over her late husband’s truck stop café, keeping hidden in the kitchen so as not to cause a scandal in Iran’s conservative society. But her brother-in-law, out of familial obligation, wants to take her as a wife and also take over the café. Meanwhile a Greek trucker who is a frequent café customer is slowly entranced — at first by her sublime cooking ability, and then by her.', 'tt0477585', '/ikEMDuDsVC9IhtLOz1n4EOgD3Kq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8480, 'Benji: Off the Leash!', False, True, False, False, 2004, 5, 'Benji and his friends try to save his mother from a puppy mill.', 'tt0315273', '/2bdNNL4ROMSWIWXIxgZpySa4kaj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8484, 'The Hollywood Complex', False, True, False, False, 2011, 6.8, 'Every spring, Hollywood hosts a very species-specific migration: kids. Thousands of aspiring child actors flock to Tinseltown for pilot season, the traditional casting period for new network and cable television shows. But unlike adult actors who pound the same star-lined pavement, kids come with their families. Many set up camp at the Oakwood, a temporary housing complex that caters to the showbiz flock. While their little Angelinas and mini Brads audition for armies of ruthless agents and TV executives, the parents must manage sky-high expectations and thin skins, including their own', 'tt1754351', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8486, 'Going Attractions: The Definitive Story of the American Drive-in Movie', False, True, False, False, 2013, 6.9, 'Once a vibrant part of American culture, drive-ins reached their peak in the late 1950s with almost 5,000 dotting the nation. Although drive-ins are experiencing a resurgence, today less than 400 remain. In a nation that loves cars and movies, why haven''t they survived?  April Wright''s lovingly made documentary--filled with archival images of hundreds of open and closed drive-in theaters and interviews with theater owners and cinema luminaries such as Roger Corman--attempts to answer that question.', 'tt2461226', '/5IueA1QSYSjwJkgHy13bJyLXUGd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8487, 'Soulless 2', False, True, False, False, 2015, 6.2, 'Max Andreev thought the best place to start a new life would be at the other end of the world. What he didn''t know, is that you can''t outrun your past.', 'tt3136646', '/wBAUnVJQLRVsUWvkO0hMN4JJyRh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8489, 'The Last Supper', False, True, False, False, 2012, 5.7, 'A group of idealistic, but frustrated, liberals succumb to the temptation of murdering rightwing pundits for their political beliefs.', 'tt0113613', '/oIgPiiM0qD1mCo1SYG3Sd0vmKTa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8495, 'Dreams of Dust', False, True, False, False, 2008, 7.1, 'Nigerian farmer Mocktar comes to Essakane, a dusty gold mining camp in Burkina Faso, seeking work. Haunted by a tragedy-laden past, Mocktar stoically adapts to the horrid working conditions of his fellow miners. Enter the beautiful widow Coumba, who shares Mocktar''s endurance but dreams of a better life. Director Laurent Salgues also penned this independent drama about greed and redemption, a 2007 Sundance Film Festival competition entry.', 'tt0899095', '/iwbyeFlMUpFCxMGMBwLBy9FvPri.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8498, 'Crossing Point', False, True, False, False, 2016, 5.1, 'A young American couple in love, Michael and Olivia vacation in Baja, but things take a frightening turn when Olivia is kidnapped by drug dealer, who demands that Michael smuggle a backpack full of cocaine-stolen from a rival cartel-over the border into the U.S. within twelve hours or else Olivia will be killed. A good Tijuana Cop is on the trail, as Michael navigates the treacherous underworld of Mexico, and he himself becomes a force to be reckoned with...', 'tt3027716', '/nnxtFh1j47tb29z2ImzYA4NFaea.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8500, 'Tio Papi', False, True, False, False, 2013, 6.1, 'It was a bachelor’s life for hard-working and fun-loving Ray Ray Dominguez (Joey Dedio) who dreamt of leaving the barrio for a more carefree existence in Miami. That’s until one day when everything changed – and he became a reluctant “Tio Papi” aka Uncle Daddy to his sister’s six children ages 6 to 16. Now, in charge of raising this energetic (and expensive) clan, Ray Ray must make important decisions on what life really is all about. Combining heart-warming drama with light-hearted comedy, TIO PAPI, directed by Fro Rojas from the original screenplay by Joey Dedio and Brian Herskowitz, is an upbeat story of life’s unexpected surprises and ultimately what matters the most – the love of family.', 'tt1986998', '/wlwdfp0DUCAr5lFdgpLCMEfwg8j.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8503, 'Winter Break', False, True, False, False, 2003, 5, 'Viscerally experience a "year off" in Aspen, Colorado with a group of recent college grads who have elected to defer graduate school and career opportunities to ski, snow-board, climb, party, fall in love and basically live life to its absolute fullest out in this spectacular mountain town.', 'tt0261992', '/jEPVFr6b9L1QSPmfAquDSNgKcrB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8504, 'Never Too Young to Die', False, True, False, False, 1986, 4.9, 'Secret agent Drew Stargrove is brutally murdered by the ruthless transexual gang leader Velvet Von Ragnar. The murdered secret agent''s son, Lance Stargrove is thrust into the dangerous and intriguing world of secret agents and espionage when he seeks revenge. Danja Deerling teams up with Lance as his sidekick and love interest.', 'tt0091621', '/19B0Aexe3Y3ONZFOmGORV0BeHZA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8505, 'Open Up to Me', False, True, False, False, 2013, 6.7, 'At first glance Maarit looks like any other woman in her early forties. There is, however, something strange under the surface. She radiates sexual energy that is hard to define.', 'tt2325909', '/rGHAJQaWdPZunsIKrZbypnc4dxb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8514, 'The Secret Invasion', False, True, False, False, 1964, 5.8, 'During World War II, convicts are recruited by the Allies for an extremely hazardous mission.', 'tt0058561', '/nrfDwP3ricNZ7GNA8ov2KAZOEiU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8515, 'Pajama Party', False, True, False, False, 1964, 4.9, 'A Martian teenager sent to prepare for an invasion falls in love with an Earth girl.', 'tt0058440', '/qCSkjCJu1NHJcYVurbcdbkYOUP4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8518, 'Ruby', False, True, False, False, 1977, 4.6, 'Strange killings occur at Ruby''s drive-in theatre, sixteen years after the murder of her gangster boyfriend.', 'tt0076644', '/zGAxPyLfDp2LGxqFb0sjJFFi9YY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8521, 'Surf Nazis Must Die', False, True, False, False, 1987, 3.7, 'When the grandson of a gun wielding woman is murdered by neo-nazi surf punks in the post-apocalyptic future, this grandma hunts them down for some bloodthirsty revenge.', 'tt0094077', '/gVECT5DHulQJyo9oqS1tdDSGowI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8523, 'Blackout', False, True, False, False, 2007, 5.9, 'First responders, journalists, shop owners, those inside the pressure-packed control center of Con Edison on West End Avenue, and other New Yorkers tell about what happened when the lights went out on July 13, 1977.', 'tt4284046', '/jH13IMZZlyk0f7G6eQLycn0JLgM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8524, 'Shelter', False, True, False, False, 2017, 5.7, 'Hannah and Tahir fall in love while homeless on the streets of New York. Shelter explores how they got there, and as we learn about their pasts we realize they need each other to build a future.', 'tt3136112', '/gwyH6OnsLG3ti9HdxLNy4Bg870N.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8529, 'A Sister''s Nightmare', False, True, False, False, 2013, 5.8, 'Police officer Jane Rydert''s life goes into a tailspin the day her older sister, Cassidy, shows up at her door after sixteen years of confinement in a psychiatric hospital.', 'tt2953240', '/752PY0BTA4Bs3uzhTAUyB8XJNgA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8531, 'The Huntresses', False, True, False, False, 2014, 5.5, 'Set in the Joseon Period, three beautiful musketeers fight against a powerful group who tries to overturn the royal family and gain absolute power. The leader of the Beautiful Three Musketeers is Jin-Ok (Ha Ji-Won), a smart and righteous woman. Hong-Dan (Gang Ye-Won) is the only married woman among the three women. Ga-Bi (Son Ga-In) is the youngest among the musketeers and excels at fighting.', 'tt3530418', '/mNbIgZsuYZLw8nSFsIASkC4M9er.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8532, 'Whirlygirl', False, True, False, False, 2006, 5.7, 'After a run in with a hot exotic dancer one wintry night straight-laced prep school student James will find himself on the ride of his life. Risking his future he follows this mysterious woman to New York City - ditching school dodging cops and partying at hot nightclubs along the way.', 'tt0364745', '/ohnbKEWnIOTglQ1kimthX3F26rR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8535, 'Light of My Eyes', False, True, False, False, 2001, 7, 'The role of the movie is about two friends, one travels to a European country And The Other One combat To blind girl falls in love and when return to Egypt together they discover the same girl that he loves his friend and she traveled to this country to search for a way modern medical help to restore its consideration.', 'tt1918984', '/wCjAIIeIXbzpqHgEg9w2MJJKVeW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8538, 'A Shot in the Dark', False, True, False, False, 2005, 6.3, 'A Shot in the Dark is the second and more successful film from the Pink Panther film series where both animated and real life sequences are mixed. A cult classic from Blake Edwards based on the play L’Idiot by Marcel Achard and Harry Kurnitz.', 'tt0058586', '/5ZNIuD2G57OlaYpc9M0MhwYiW1a.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8544, 'Fetish', False, True, False, False, 2010, 8.5, 'When yuppie Korean-American lawyer Peter brings beauteous bride Sookhy home to suburban New Jersey, she seems withdrawn, English-challenged and easily bullied by her mother-in-law. But fate abruptly takes Peter out of the picture, leaving a curiously unperturbed Sookhy (who''s descended from a line of female shamans) free to force her company on the glamorous Caucasian couple next door.', 'tt1145152', '/nnShuzVjdBWZ659ouQDTBsbNXoE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8545, 'U.F.O.', False, True, False, False, 2012, 3.1, 'A group of friends awake one morning to find all electricity and power shut off and an immense alien aircraft hovering in the air above their heads. Suddenly this regular group of friends is battling to survive as the entire human race is threatened by the alien army hovering ominously above. Aliens announce a hostile takeover of Earth by cutting off all power and communications, prompting a small band of survivors to fight for the human race in this sci-fi action-adventure featuring Jean-Claude Van Damme', 'tt2040578', '/qh40LJPtEl04BNk9soYwL9w6cPt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8550, 'Why We Fight: Divide and Conquer', False, True, False, False, 1943, 7.2, 'The third film of Frank Capra''s ''Why We Fight" propaganda film series, dealing with the Nazi conquest of Western Europe in 1940.', 'tt0173769', '/A0ivckU4DK8g70dJvVaXxExUMtC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8552, 'Island', False, True, False, False, 2011, 5.8, 'Based on ''Jane Rogers'' acclaimed novel, "Island" is a tale of yearning and retribution. Abandoned at birth, Nikki Black has spent most of her life in care sustained only by fairy-stories. Incapable of love, fearful and desperate for revenge, she decides to find her birth mother, confront her, and bitter enough to consider murder. She travels incognito to a remote Hebridean island where Phyllis now lives as a recluse with Calum, her son.', 'tt1185393', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8560, 'Dreams on Spec', False, True, False, False, 2007, 6.3, '"Dreams on Spec" takes an intimate look at how far people will go - and how much they will sacrifice - for the chance to pursue their dreams. This feature-length documentary delves into the lives of three aspiring Hollywood screenwriters as they pour their hearts into their spec scripts, pitch their ideas to anyone who will listen, go to meetings, hold table reads, and work at low-level day-jobs - all in the hopes of one day seeing one of their beloved creations made into a movie. These poignant portraits are intercut with wisdom from a "Greek Chorus" of superstar Hollywood creative-types like James L. Brooks, Nora Ephron, Gary Ross, and Carrie Fisher to forge a funny and compelling look at inspiration, creativity, and solitude in the movie industry.', 'tt0490117', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8561, 'Falling', False, True, False, False, 2005, 7.2, 'Five former high school friends reunite for an evening of fun, remembrance and sadness in this Altmanesque portrait of female friendship. A look at how youthful idealism turns into adult pragmatism, and how one can fight such changes, from the director of Free Radicals.', 'tt0832347', '/3Ir9pUbngkmwLnGTmkom7HRBOJE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8562, 'Children of God: Lost and Found', False, True, False, False, 2007, 6.8, 'A first-person account of growing up in an evangelical Christian cult.', 'tt0806136', '/cVOZTpblV9vIle7u235xdtpanRY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8563, 'American Yakuza', False, True, False, False, 1993, 5.7, 'When Nick Davis leaves prison after one year in solitaire, he is hired to operate forklift in a warehouse in the harbor owned by the Japanese Yakuza patriarch Isshin Tendo. The place is assaulted by the Italian Mafia leaded by Dino Campanela and Nick rescues and saves the life of Shuji Sawamoto, who is the representative of Yakuza interests in America. Shuji hires Nick to work for Yakuza and becomes his godfather in the family after his oath to join Yakuza. However, Nick is a lonely FBI undercover agent assigned to penetrate in the criminal organization. When the FBI discovers that Campanela is organizing a massive attack to destroy the Yakuza, Nick''s boss Littman calls off the operation to leave the dirty work to the Italian Mafia. But the connection of Nick with Shuji and his goddaughter Yuko forces him to help his Japanese family.', 'tt0109099', '/p3GRkFk6WwZQafthh3qvgMHbrst.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8565, 'Last Call', False, True, False, False, 2002, 6.5, 'Danny and Phil O''Donnell are chronically underachieving cousins, who are forced to run the family pub, in order to save their crazy Uncle Pete from jail and financial destitution. The boys instigate a number of hilarious schemes, from turning the pub into a strip club to a high school speakeasy, just to keep it afloat.', 'tt1472583', '/xjhvoNtJaihX6CiRpGD9oPsLehu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8566, 'Across the River', False, True, False, False, 2013, 5.5, 'After a wildlife biologist (Marco Marchese) becomes trapped on the wrong side of the river while collecting data, he comes across an abandoned village. Taking shelter, he begins to suspect he may be sharing the grounds with more than just boar and deer.', 'tt2836202', '/sPF4vXawuzGxhK41QuKNhgj0e08.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8567, 'In My Sleep', False, True, False, False, 2010, 5.5, 'Marcus (Philip WInchester) wakes up in the middle of a cemetery half naked and has no idea how he got there. Urgent questions pound inside his head. "Where was I last night? Who was I with?" Marcus suffers from parasomnia, a rare sleep disorder which causes him to do things in his sleep which he cannot remember the next day. His situation takes a horrifying turn when Marcus wakes up covered in blood with a knife at his side. Then, the wife of his best friend is found stabbed to death. Marcus is terrified to put together the pieces - could he have murdered her in his sleep to hide a dark secret between them? Mysterious phone calls make Marcus believe someone is watching him. He investigates his own nocturnal activities, desperate to figure out what happens after he goes to sleep at night. His journey to discover the truth ends in a shocking discovery.', 'tt0326965', '/sv9o6VYUQM2AbfmlTwDTn9238R5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8569, 'Tom Brown''s School Days', False, True, False, False, 1940, 6.7, 'When private tutor Thomas Arnold (Sir Cedric Hardwicke) becomes headmaster at Rugby, a boy''s preparatory school in England, he puts into place a policy of strict punishment for unruliness and bulying. Arnold finds an ally in Tom Brown (Jimmy Lydon), a new student who is subjected to hazing and abuse by a group of older boys and is pressured by his friends to keep quiet about it. Fed up, he leads his fellow classmates in an underground rebellion against their tormentors. But certain unspoken rules still apply at the school and Brown loses his hero status when he is accussed of breaking the Rugby code of silence.', 'tt0033169', '/yrlt6ribkNHPyNuDRCPdCDTo68d.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8572, 'The Cliff', False, True, False, False, 2016, 5.2, 'A mass suicide involving members of a cult takes place in the Canary Islands. Gabriel''s younger sister, Cordelia, who he hasn''t seen for years, was one of the cult followers. Gabriel decides to travel there and find out what happened.', 'tt4176928', '/BP8Ul0eVrdGMyiXdK4eMzGLFPE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8577, 'Anjelah Johnson: The Homecoming Show', False, True, False, False, 2013, 6.7, 'Former Raiders cheerleader turned stand-up comedienne, Anjelah Johnson has been dazzling audiences on the big screen, on television and during her live performances across America with her hysterical characters and ironic humor. With a huge female following and the ability to cross over to both Mid-western mainstream and Hispanic fan-bases, Anjelah''s new show leaves you smiling after an evening full of belly-laughs. A new comic super-star has emerged.  This side splitting release from stand-up comedian and former Oakland Raiders cheerleader Anjelah Johnson captures a live performance by the funnywoman, recorded live in hometown of San Jose.', 'tt3499286', '/aDqKXJHaYPuKzXL0eUOvn0eF5WB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8578, 'Beneath', False, True, False, False, 2013, 3.7, 'A crew of coal miners becomes trapped 600 feet below ground after a disastrous collapse.  As the air grows more toxic and time runs out, they slowly descend into madness and begin to turn on one another.  Inspired by true events.', 'tt2991296', '/1VkjTjqr1T1FyTtxwrTavCvKsiO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8579, 'Accidental Love', False, True, False, False, 2015, 4.1, 'A small town waitress gets a nail accidentally lodged in her head causing unpredictable behavior that leads her to Washington, DC.  Sparks fly when she meets a clueless young senator who takes up her cause - but what happens when love interferes with what you stand for?', 'tt1137470', '/cIoH8SS8famTdM7gZuivudgWFGA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8584, 'Jim Norton: Please Be Offended', False, True, False, False, 2012, 7.1, 'Caustic comedian and best-selling author Jim Norton pulls no punches in his first EPIX comedy special, going after jaw-dropping laughs not intended for the faint of heart. Among his targets: the national hypersensitivity epidemic - leading celebrities, talking heads and regular Joes alike to get offended by just about anything. Norton''s got a message for those people, and it''s in the title.', 'tt2147822', '/A0lsQzDBxA041LDtsRs7LVLWAmH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8586, 'Plastic Galaxy: The Story of Star Wars Toys', False, True, False, False, 2014, 6.5, 'Plastic Galaxy explores the ground breaking and breathtaking world of ''Star Wars'' toys. Through interviews with former Kenner employees, experts, authors, and collectors, the documentary looks at the toys'' history, influence, and the passions they elicit today.', 'tt3648510', '/uJxlQhzz9gYDUvHJ6qaTc166JuZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8587, 'The Big Squeeze', False, True, False, False, 1996, 5.1, 'Benny, a hapless grifter, is thrown off a train after cheating at cards, and into the life of Tanya, a lovely bartender. Tanya has been supporting her husband, a former ballplayer, since his accident, waiting for an insurance settlement that will solve all their financial woes. But when it comes, he refuses to share it with her, claiming with newfound piety that money is the root of all evil. She then enlists the help of Benny to concoct a scheme to get her fair share of the settlement, and he finds one: a local Catholic mission needs money for repairs and Benny decides to create a miracle that will encourage husband Henry to fork over the money - which Benny and Tanya will intercept and share.', 'tt0115680', '/eeGJWQgI98iFPfV95CdCK3oGYB8.jpg');
