insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8603, 'The Curse of Sleeping Beauty', False, True, False, False, 2016, 4.2, 'Thomas Kaiser inherits an ancestral mansion that has been in his family for generations — only to learn that he has also inherited an ancient curse stemming back to the Crusades. Forced into his new role as “protector” — the guardian appointed to keep the evil demons in the house at bay — Thomas teams with an ambitious local realtor and paranormal cleric to unravel the mystery of the house, while struggling to awaken the beautiful Briar Rose, held captive in a terrifying netherworld seen previously in his dreams.', 'tt4118606', '/wO6If1l2Uv5RIngEyZmcAcI2k28.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8607, 'Slaughterhouse', False, True, False, False, 1987, 5.4, 'The owner of a slaughterhouse facing foreclosure instructs his 350lbs, mentally retarded son to go on a killing spree against the people who want to buy his property.', 'tt0093990', '/8k7w4Jh9KWMqCoZzpBaoZPzqNdr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8612, 'Bonnie''s Kids', False, True, False, False, 1973, 6.1, 'Sisters Myra and Ellie have finally had enough of their miserable, dead-end lives. When their stepfather Charley (the titular "Bonnie" being long dead) tried to rape Myra, Ellie ventilates him with a shotgun, and the pair run off to their wealthy uncle''s mansion in El Paso. From that point on, the two undergo a transformation in their personalities, and start to enjoy living their lives on the wild side.', 'tt0083677', '/AuEmkkHP5jBUExsE2NLL0Y2nDVC.jpg');
