insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8614, 'Human Lanterns', False, True, False, False, 1982, 6.6, 'This is it: one of the most talked about cult films ever made -- a maniacal masterpiece of the macabre, the martial arts, and the just plain weird. Shaw''s first international star Lo Lieh stars as the insane swordsman who makes Chinese lamps from, yes, the skin of his enemies'' sisters, mistresses, and wives. Although insanely conceived, it is beautifully filmed and unashamedly performed by a first rate cast of sexy starlets and kung-fu favorites.', 'tt0084586', '/keVpaMXzRKECD7C3tzfIOZKnLLi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8615, 'Alaska: Spirit of the Wild', False, True, False, False, 1998, 7.2, 'Alaska... Here, in this vast and spectacularly beautiful land teeming with abundant wildlife, discover the "Spirit of the Wild." Experience it in the explosive calving of glaciers, the celestial fires of the Aurora Borealis. Witness it in the thundering stampede of caribou, the beauty of the polar bear and the stealthful, deadly hunt of the wolf pack.', 'tt0130445', '/bVvmVUDs2rJgJ46j7Ja66CDQD5j.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8616, 'MVP: Most Valuable Primate', False, True, False, False, 2000, 4.1, 'Jack is a three-year-old chimpanzee who has been the subject of a long-term experiment by Dr. Kendall, a researcher who been teaching Jack to communicate through sign language. ack scrambles onto the ice in the midst of practice for Steven''s junior league hockey team, and he and his teammates discover the monkey has a natural talent for the game.', 'tt0196106', '/aMlANWb7FgXbiQNurEasuU9fOWb.jpg');
