insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8620, 'Bottled Life: Nestle''s Business with Water', False, True, False, False, 2012, 6.8, 'Do you know how to turn ordinary water into a billion-dollar business? In Switzerland there''s a company which has developed the art to perfection - Nestlé. This company dominates the global business in bottled water. Swiss journalist Res Gehringer has investigated this money-making phenomena. Nestlé refused to cooperate, on the pretext that it was "the wrong film at the wrong time". So Gehringer went on a journey of exploration, researching the story in the USA, Nigeria and Pakistan. His journey into the world of bottled water reveals the schemes and strategies of the most powerful food and beverage company on our planet.', 'tt2072910', '/zARhxk7lTdmsipZOrppWds7US9r.jpg');
