insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8635, 'Cameron Esposito: Marriage Material', False, True, False, False, 2016, 6.1, 'Cameron Esposito decided to do something a bit different for her bachelor party: record her first standup special. Filmed in Cameron''s hometown Chicago just two days before her wedding, the special covers everything from the secrets of a women''s locker room to gun control to marriage equality and proves that, yes, Cameron Esposito is Marriage Material.', 'tt5275780', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8639, 'Parasomnia', False, True, False, False, 2008, 5.2, 'Laura Baxter is a young woman, literally a "sleeping beauty," who suffers from a medical condition called "parasomnia." A childhood accident victim, she is actually sleeping her life away, awakening briefly on rare occasions. Art student Danny Sloan falls in love with her, unaware that her hospital neighbor, a terrifying mass murderer and mesmerist named Byron Volpe has other, more sinister plans.', 'tt0922547', '/k4Rx1dwoKkZrtSD0mZBRBo22jmb.jpg');
