insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8659, 'Attack Force Z', False, True, False, False, 1982, 5.6, 'Tim Burstall directs then-up-and-comers Mel Gibson and Sam Neill in this action-packed Cannes Film Festival selection about the grim realities of World War II, a gritty drama based on actual events. Sent to rescue survivors from the site of a plane crash in the South Pacific, Capt. P.G. Kelly (Gibson) and his elite squad of Australian commandos must keep tabs on a defecting Japanese official who could hold the secret to peace.', 'tt0083591', '/9u2ry5cW1vq8MzNFgph6iNAqM2C.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8667, 'The Architect', False, True, False, False, 2016, 4.8, 'When a couple sets out to build their dream house, they enlist the services of an uncompromising modernist architect, who proceeds to build HIS dream house instead of theirs.', 'tt3180912', '/2RnzL9Rh5F0dC5we7JihqyYpmgJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8668, 'The Hooligan Factory', False, True, False, False, 2014, 4.8, 'Danny wants something more. Expelled from school and living in his grandfathers flat, he longs to live up to the image of his estranged father Danny Senior', 'tt2360446', '/3KjrgwU3ylj71iokFo7YJ4qNXbn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8672, 'Silent Heart', False, True, False, False, 2014, 7.1, 'The prestigious Danish filmmaker Bille August, winner of an Academy Award and two Palme d''Or in Cannes, returns with a highly personal drama. Three generations of a family gather over a weekend. The sisters Sanne and Heidi have accepted their terminally-ill mother’s desire to die before her disease worsens; but, as the weekend progresses, their mother''s decision becomes harder and harder to deal with, and old conflicts come to the surface.', 'tt3335048', '/ar3hlLoKPcUM9xKBEMd3SUHoqyo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8673, 'The Reconstruction of William Zero', False, True, False, False, 2015, 5.4, 'A geneticist wakes up from an accident with only fragments of his memory is forced to relearn who he is via his twin brother. But as he digs deeper, he discovers he might not be who he thought at all.', 'tt1867091', '/lbrl38GT5i46CykwQNmjo0w0LjP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8674, 'Apollo 13: To the Edge and Back', False, True, False, False, 1994, 8.2, 'Apollo 13: To the Edge and Back tells the gripping, true story of the catastrophic flight of the Apollo 13 and the heroic struggle to bring the astronauts back alive. With first-hand accounts from the pilots, their families, and the people of mission control, it documents a thrilling struggle against time and all odds and serves as a reminder that, in the words of James Lovell, "We do not realize what we have on Earth until we leave it."', 'tt0180443', '/eFjtsjq8gLovE8f3MRrPB2c9aS6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8675, 'Are All Men Pedophiles?', False, True, False, False, 2012, 6.4, 'For the first time a documentary examines the global impact of pedophilia from a cultural and professional perspective. We live in a society that condemns pedophiles, though biological instinct and world cultures throughout history suggest that an attraction to adolescents is as natural as it is unavoidable. The fashion industry on the one hand sexualizes ever-younger girls while those who act on these instincts are reviled. The apparent hypocrisy at the heart of society forces the question: What do we mean when we talk about Pedophilia? The film explores the many dangers children face and exposes the systematic violation of children rights by societies, presenting the testimony of both victims and perpetrators. It also looks at how the pedophilia hysteria that has led to the mass incarceration adults and children. In conclusion, we are faced with both the contradiction of a society which sexualizes youth and the question: Are All Men Pedophiles?', 'tt2072045', '/cQ2ZQEv0nbiOSLJikc224JmCVRM.jpg');
