insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8799, 'Tribulation', False, True, False, False, 2000, 5, 'Tom Canboro is a police detective with a Christian sister, Eileen, a brother, Calvin, a wife, Susan, and eccentric brother-in-law, Jason. One night, Jason seemingly goes insane and tries to kill Eileen, calling her a "hater". Tom soon realises this may be a conspiracy going as far as devil worship, but as he speeds in his car to get help, he suddenly looses control and crashes. Waking up in a strange hospital, he finds years have passed and people all over the world are wearing the mark of 666 on their right hands, and all those who don''t are being killed off. Franco Macalusso, the believer in world peace Jason was fond of, now controls the world, and is trying to bring the world together as it was at the tower of Babel. Meanwhile, the "Haters" or Christians are hiding out and airing TBN tapes on the air for people to realise the Messiah is really the Antichrist, but their leader, Helen Hannah, is arrested... Written by The Extra in the Background', 'tt0202236', '/cTXjjh8Vku9DIBtmLrGu0cz5tNX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8804, 'Elon Gold: Chosen and Taken', False, True, False, False, 2014, 7, 'With a manic wit that transcends race and religion, sharp-tongued comedian Elon Gold explores the ridiculous things people manage to get away with.', 'tt2982306', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8805, 'Teenagers from Outer Space', False, True, False, False, 1959, 3.8, 'A young alien falls for a pretty teenage Earth girl and they team up to try to stop the plans of his invading cohorts, who intend to use Earth as a food-breeding ground for giant lobsters from their planet.', 'tt0053337', '/8sjXliauMasJNt1G1MLerwsbk1I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8806, 'Meth', False, True, False, False, 2006, 7.6, 'But it is a devastating expose of party-n-play culture. It''s hard to depict methamphetamine use with any sensitivity, but this movie''s dry and frank style bring this plague into startling focus. It''s very compassionate and gives a lot of context and insight into how Gay culture and drug culture meet. It is heartbreaking to know that the majority of the men interviewed for this movie are HIV-positive. This movie includes graphic sexual content and vivid depictions of drug use, but it serves a very valuable purpose. it is an eloquent call for social responsibility.', 'tt0783550', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8809, 'Champion', False, True, False, False, 2002, 6.6, 'This biopic tells the true story of Korean boxer Kim Deuk-gu (played by Oh-seong Yu). Born into poverty, Kim used boxing to free himself from his bleak surroundings, eventually becoming the world''s top lightweight. Tragically, Kim''s life was cut short when he died from a blood clot in his brain during a championship fight with Ray "Boom Boom" Mancini. Directed by Kyung-Taek Kwak, this film co-stars Min-seo Chae, Seung-won Yun and Byeong-seo Kim.', 'tt0325165', '/9ZhnsN9z7CITWEsopyn4Z6UxMKh.jpg');
