insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8817, 'Revelation', False, True, False, False, 1999, 5.1, 'Since A.D. 50, a mysterious sacred artifact known as the Loculus has been at the center of a bloody clandestine war. Missing for a few hundred years, the puzzling relic has suddenly reappeared in the present day, bringing with it a terrible secret that could spell doom for all of mankind. Now it''s up to Magnus Martel (Terence Stamp) to subvert a murderous secret society and uncover the truth behind the ancient treasure.', 'tt0278675', '/tWyXh7WZZOshB5CmfdDy40MPVDd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8818, 'Spark: A Space Tail', False, True, False, False, 2017, 4.5, 'Spark, a teenage monkey and his friends, Chunk and Vix, are on a mission to regain Planet Bana - a kingdom overtaken by the evil overlord Zhong.', 'tt3228088', '/4zj2KfykL4qvYCrbMv46Yd5vnYM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8820, 'The Gamers: Hands of Fate', False, True, False, False, 2013, 6.8, 'The Gamers: Hands of Fate follows Cass (Brian Lewis) as he sets out to win a collectible card game world championship… and a date with Natalie (Trin Miller), one of the game''s top players.  Meanwhile in Countermay, a world far across time and space, Myriad (Samara Lerman) begins to suspect that fate has stacked the deck against her as she attempts to save her kingdom from a ravenous army of the undead.  Set at Gen Con Indy, a massive midwest games convention, this fantasy comedy from the makers of Dorkness Rising and JourneyQuest presents a new chapter in the epic, decade-spanning tale that began in The Gamers.', 'tt2312390', '/A8hIU7niqS5klfkcRbFVSrC4UAk.jpg');
