insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8826, 'Reparando', False, True, False, False, 2010, 6.7, 'On the morning of June 18. 1954, the US CIA dropped leaflets in Guatemala City demanding the resignation of the president. Guatemala was ravaged by Civil War for the next 36 years. But hope is rising. In the midst of incredible odds, victims have been transformed into champions who willfully embrace the pain of their past to help repair the next generation. This is their story. Shorty - a former gang member who is now a pastor, and Tita - a woman who started a school in Guatemala''s most notorious slum have joined forces to repair La Limonada.', 'tt1664825', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8827, '5ive Girls', False, True, False, False, 2006, 4.4, 'Five wayward teenage girls are sent to a reformatory and discover they possess unique powers to battle the ancient demon, Legion, which holds thrall over the sinister institution.', 'tt0494199', '/9EKL1Ta42cj9rSqopfu3QJFE3G1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8828, 'Leave', False, True, False, False, 2011, 5.9, 'Henry Harper is a successful novelist who has it all. But after surviving a recent trauma he finds himself haunted by a dream that terrifies him. Convinced that the only way to understand what the dream means is to write his way through it, Henry decides to go to a remote second home to begin work on his next novel, a thriller. While on his way there he encounters a strangely familiar drifter who confronts him with information that threatens to turn everything he knows to be true, upside down. Written by producer', 'tt1446695', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8834, 'The Conclave', False, True, False, False, 2006, 6.5, 'In 1458, five years after the fall of Constantinople to the Moslems, eighteen cardinals meet to elect a new pope. Rodrigo Borgia, a 27 year old cardinal learns to play a very dangerous game.', 'tt0452011', '/oS0vbkCPfbl8mn1Hjh8O2kDmxaZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8835, 'Three Guys Named Mike', False, True, False, False, 1951, 6.2, 'A stewardess becomes romantically involved with an airline pilot, a college professor, and a successful businessman...all of whom are named Mike. When the three find out about each other, she has to decide which one she loves the most.', 'tt0044125', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8837, 'The 12 Dogs of Christmas', False, True, False, False, 2005, 5.4, 'This holiday tale has all the elements of a classic heartwarmer--cuddly dogs, small town spirit, and even a full-fledged Christmas pageant. Set during the meager years of the Great Depression, 12 DOGS OF CHRISTMAS follows the story of Emma, a 12-year-old who is sent to live in Doverville, a rural town, with her aunt. The town has, like the rest of the nation, fallen on lean times and seem to have lost all interest in celebrating. However, Emma manages to enlist the town''s dogs into a colorful holiday pageant that reminds the inhabitants that even the Great Depression cannot ruin the spirit of Christmas.', 'tt0405615', '/9eFNLBmRL8hcHGFBPxcUejq3Pfl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (8842, 'American Scary', False, True, False, False, 2006, 6.7, 'A fond remembrance of and tribute to the uniquely American institution of the horror movie host.', 'tt0371530', '/eVusFYR1dNTJb9ZFK7peDUjrzk6.jpg');
