insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9046, 'Imaginary Friend', False, True, False, False, 2012, 5.7, 'During her traumatic childhood with an abusive father, Emma relied on an imaginary friend for strength. She is now an artist struggling with mental illness living with her apparently loving husband, Brad. But when Emma''s imaginary friend starts reappearing, she knows she''s losing her grip. Desperate to save her marriage and show Brad she is in control, Emma takes drastic measures to free herself of her torment and her imaginary friend.', 'tt2171735', '/nmNa0vlptwMxwmf4kVQ05wCLKiY.jpg');
