insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9050, 'Daddy', False, True, False, False, 2015, 5.7, 'Based on A. Galych''s play "Matrosskaya Tishina", "Papa" tells a story of a Jewish father who dreamed of seeing his son perform on a stage in front of huge audiences, he dreamed of seeing him as the greatest violinist of his time. To achieve the goal he taught his son Dodik how to play the violin from the yearly age. When Dodik grew up he left the small town he and his father lived in to study in the Moscow Conservatory leaving his past behind. But one day he has to choose either to loose his father or everything he has achieved.', 'tt0416099', '/6OGMy8WnucKZYvGrHbyQQUXhQQc.jpg');
