insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9054, 'Neon Maniacs', False, True, False, False, 1986, 5.1, 'A group of teenagers in San Francisco discover a nest of homicidal monsters living in a tower of the Golden Gate Bridge, but when they try to tell authorities, no one believes them.', 'tt0127045', '/sqiPRc9eCdG6yjjWQ6NIuCMq4fE.jpg');
