insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9078, 'The Amazing Transparent Man', False, True, False, False, 1960, 4, 'An ex-major forces a scientist to develop a invisibility formula, with which he plans to create an invisible army and sell it to the highest bidder. However there are side effects to the formula.', 'tt0053593', '/xA7pkVhCxkvwD4B9mQ9G8Nub234.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9079, 'Honest Man: The Life of R. Budd Dwyer', False, True, False, False, 2010, 7.3, 'Honest Man: the Life of R. Budd Dwyer is a movie about politics and corruption, suicide and survival. The film chronicles Dwyer''s meteoric rise to political power and examines the bribery scandal and subsequent trial that pushed him to his breaking point. Honest Man also delves into the controversy and consequences of the uncensored airing of Dwyer''s death on television stations worldwide. Honest Man reveals a story that has remained untold for over 24 years. The film features exclusive new interviews, including William Smith, the man whose testimony convicted Dwyer, and Dwyer''s widow Joanne--her last interview before her death in 2009. Was Dwyer venal, or a victim? Did he kill himself because he couldn''t live with being guilty, or because he couldn''t live with being innocent? Honest Man allows audiences to judge for themselves.', 'tt1623777', '/5OiuzgU9rZaUdG7oOMXan0Bp1rB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9081, 'Margaret Cho: PsyCHO', False, True, False, False, 2015, 6.1, 'Three-time Grammy and Emmy nominated comedian Margaret Cho performs in front of a live audience in this provocative and hilarious comedy special event, tackling off-limits issues from Boko Haram to female empowerment with her razor sharp insight and wit.', 'tt4872998', '/AqYsQMoJj6Wf9GCoIx6Fvl1o8n9.jpg');
