insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9095, 'The Children of Sanchez', False, True, False, False, 1978, 6.4, 'Hall Bartlett directs the rural drama The Children of Sanchez, based on the tome The Children of Sanchez: Autobiography of a Mexican Family written by Oscar Lewis in the ''60s. Anthony Quinn stars as the widowed Jesus Sanchez, a poor farmer struggling to provide for his family in Mexico City. Also starring Lupita Ferrer as Consuelo and Stathis Giallelis as Roberto. This is the last film in the 50-year career of international star Dolores del Rio, who plays the Grandma. Jazz-pop performer Chuck Mangione was nominated for a Golden Globe and won a Grammy award for his original musical score.', 'tt0077326', '/vm7hVzXdIjPeiIfP2BTeQYL7DaL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9107, 'Pig', False, True, False, False, 2011, 5.8, 'A man wakes up alone in the middle of the desert with a black hood on his head and his hands tied behind his back. At death''s door, he is discovered by a woman living alone in the desert and is nursed back to health. Upon regaining consciousness, the man realizes he has amnesia, and has no idea who he is. His only clue, a piece of paper in his pocket with the name "Manny Elder" on it, sends him on a journey to Los Angeles to discover his past. But things and people are not what they seem and clues lead to something bigger and more unusual than the man could have ever imagined.', 'tt1262414', '/4kHWdEVuZ37CKJKWCQvNY33Fjxk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9108, 'Dick Tracy Meets Gruesome', False, True, False, False, 1947, 6.2, 'A gang of criminals, which includes a piano player and an imposing former convict known as ''Gruesome'', has found out about a scientist''s secret formula for a gas that temporarily paralyzes anyone who breathes it. When Gruesome accidentally inhales some of the gas and passes out, the police think he is dead and take him to the morgue, where he later revives and escapes. This puzzling incident attracts the interest of Dick Tracy, and when the criminals later use the gas to rob a bank, Tracy realizes that he must devote his entire attention to stopping them.', 'tt0039320', '/y6yNblyjvQd2ElZvDXIlabKSF83.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9109, 'Los Punks: We Are All We Have', False, True, False, False, 2016, 6.8, 'Los Punks: We Are All We Have is an intimate documentary about the teens and young adults who find meaning in the thriving punk rock scene in the backyards of South Central and East Los Angeles.', 'tt4934152', '/vnZ9f9L68EsYX7CBYlEyLvNKs9i.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9113, 'Love and Bullets', False, True, False, False, 1979, 5.8, 'Charles Bronson is out for revenge in this action drama. Jackie Pruit (Jill Ireland) is the girlfriend of notorious gangster Joe Bomposa (Rod Steiger). When it looks as if Jackie''s life is being threatened by Bomposa''s goons, the FBI moves in to protect her, in hopes that she''ll have incriminating evidence that the Bureau can use against Bomposa in court. Veteran agent Charlie Congers (Bronson) is assigned to watch over Jackie, and while it soon becomes obvious that she knows almost nothing about Bomposa that would be of any use to the FBI, he also falls in love with her. However, Bomposa decides that it would be a lot more convenient to have Jackie out of the way, and he orders her to be executed. Bomposa''s henchmen manage to slip through FBI security and murder her, but now they have to answer to the angry and vengeful Congers.', 'tt0079488', '/9J20Wk4mL4O4cRU3SNd1yqaWmyC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9116, 'The Big Night', False, True, False, False, 1951, 6.4, 'A film noir treatise about the coming of age of a young man is beautifully realized by the great Joseph Losey. John Drew Barrymore zigzags through the sordid vortex of downtown Los Angeles while seeking vengeance on the man who beat his Father. This superbly crafted “trial by fire” tale with memorable dialogue and shaded photography co-stars Preston Foster, Joan Lorring, Harold St. John and Dorothy Comingore', 'tt0043340', '/oOn0C6aJdpMUiJTgNmk7s0beH95.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9118, 'Long-Term Relationship', False, True, False, False, 2006, 6.2, 'Tired of the unsatisfying singles scene, Glenn (Matthew Montgomery) answers a personal ad and meets Adam (Windham Beacham), a handsome Southern man seeking a long-term relationship. The two hit it off immediately, but their initial attraction is soon put to the test. Glenn''s gay friends say he''d be happier playing the field again, but his straight pals encourage him to work at the budding relationship in director Rob Williams''s romantic comedy.', 'tt0494253', '/fUhNf52IRhop54Popynpd0qDzlR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9119, 'P.U.N.K.S', False, True, False, False, 1999, 4.9, 'A group of misfit teens evade a corrupt scientist when they steal and attempt to return a suit that delivers supernatural strength to its wearer.', 'tt0130172', '/ikX7XvF7PuaEZSEeF4pTzYhu0tm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9120, 'Helena from the Wedding', False, True, False, False, 2010, 4.5, 'Newlyweds Alex and Alice host a New Year''s Eve party for their closest friends at a remote cabin in the mountains. However, when an unexpected guest shows up, the group''s facades begin to crumble.', 'tt1394211', '/48vApZYc49WqbUzqg1Q7qiAu62z.jpg');
