insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9126, 'Catch.44', False, True, False, False, 2011, 4.6, 'The lives of three female assassins take a sudden turn when their charming boss lures them into one last job. They soon find themselves thrust into a bizarre situation involving a psychotic hit man, a grizzled trucker and a delusional line cook.', 'tt1886493', '/mbDQA2a3H9CbEHr7bDkaqxAGDO1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9129, '11:11', False, True, False, False, 2004, 3.6, 'Eighteen years after the murder of her parents, Sara Tobias searches for the meaning of the numbers ''11:11'' that was scratched in blood beside her mother''s body. Following three sudden murders, supernatural events are unleashed as she gets closer to the truth.', 'tt0396401', '/A9iemdircdmA0PRRXhDkouffy9h.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9137, 'The Time Travelers', False, True, False, False, 1964, 5.1, 'A time travel experiment that was supposed to produce a window into time turns out to be a portal instead. One of the experimenters steps through into a not-too-distant-future world that has been destroyed by nuclear war. Some of the others follow, but then the portal phases out and they can''t get back. Things just get worse after that. They run across a rocket that has landed to escape pursuing enemies, bearing scientists who survived the war, and many android "slaves." The time travellers are invited to escape when the ship is again ready to blast off - but just before that happens, the scientits'' enemy returns and fires on the sitting-duck ship. A very bad day for the scientists turns terminal at that point, and the 20th-century Earthlings barely escape with their skins.', 'tt0058659', '/cc3deOS6bejonPRPLsbZBg1LlPN.jpg');
