insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9148, 'Snow White', False, True, False, False, 2005, 5.8, 'The life of 20-year-old Nico from upper class Zurich is all party, sex and drugs when she meets Paco, the front man of a rap band. She finds herself drawn to his independent, creative and profound way of life, so different from her own. With naiveté and infinite self-confidence, she tries to adopt his lifestyle - without anticipating the consequences.', 'tt0434345', '/gig1yOIhv6WLULwvKmUzxwlQPhA.jpg');
