insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9150, 'Foreign Intrigue', False, True, False, False, 1956, 5.9, 'Millionaire Victor Danemore, living on the French Riviera, dies suddenly of a heart attack. His secretary, Dave Bishop, wants to know more about his employer''s life. Surprisingly, not even his young wife knows anything about her husband''s background or how he earned his fortune. Clues lead Bishop to Vienna and Stockholm, where he learns that Danemore was black-mailing people who cooperated with the Nazis during WW2.', 'tt0049224', '/zff11yD6HZeoAsXb2JgbWOe2tBq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9152, 'Barnum!', False, True, False, False, 1986, 7.5, 'Barnum the musical traces Phineas Taylor Barnum''s career from 1835 to 1881 when he joined James A. Bailey to form the circus which was called The Greatest Show on Earth. Barnum is a defender of "the noble art of humbug" with a philosophy, and has a free wheeling ambition to make a fortune. He buys the oldest woman in the world, named Joyce Heath, as a sideshow attraction. Barnum builds a museum of curiosities supported by his wife Charity, who would like him to settle down. Written by Jenny Evans', 'tt0090702', '/rmuBuEaUqMENa9V22bXfsPGktMa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9153, 'Living Proof', False, True, False, False, 2017, 8, 'Living Proof is a 2008 Lifetime Television movie, directed by Dan Ireland, starring Harry Connick, Jr. The film is based on the true life story of Dr. Dennis Slamon and the book HER-2: The Making of Herceptin, a Revolutionary Treatment for Breast Cancer by Robert Bazell. Vivienne Radkoff wrote the film script, and is also one of the film''s executive producers. Renée Zellweger is another executive producer, together with Neil Meron and Craig Zadan. Tammy Blanchard, Amanda Bynes, Jennifer Coolidge, Angie Harmon, John Benjamin Hickey, Regina King, Swoosie Kurtz, Paula Cale Lisbe, Amy Madigan, Bernadette Peters, and Trudie Styler are featured in the supporting cast. The film premiered on October 18, 2008, for Lifetime''s "Stop Breast Cancer for Life" public service and advocacy campaign, during the National Breast Cancer Awareness Month', 'tt1229367', '/9vuqQniFUJEeMJVh3suI2wG66W2.jpg');
