insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9359, 'Mr. Fix It', False, True, False, False, 2006, 5.5, 'Lance Valenteen (David Boreanaz) makes a living as "Mr. Fix It," a man who gets hired by men that have just recently been dumped by their girlfriend. Lance dates the guys'' ex-girlfriend and becomes the worst date possible, sending the girl back into her ex-boyfriends arms. But when Lance gets hired by Bill Smith (Pat Healy) to get Sophia Fiori (Alana De La Garza) back, Lance, for the first time, starts falling for one of his marks.', 'tt0416051', '/dBgEdBeMg4KDJaWGRUmPKppQg2I.jpg');
