insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9600, 'Charlie Chan in the Secret Service', False, True, False, False, 1944, 6.5, 'Charlie Chan is an agent of the U.S. government working in Washington DC and he is assigned to investigate the murder of the inventor of a highly advanced torpedo. Aiding Chan is his overeager but dull-witted Number Three son Tommy (Benson Fong) and his Number Two Daughter Iris Chan (Marianne Quon).', 'tt0036706', '/uwr8hmiV2TIcbExX3MWRGnglURN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9602, 'The Mind Snatchers', False, True, False, False, 1972, 5.6, 'A German scientist works on a way of quelling overly aggressive soldiers by developing implants that directly stimulate the pleasure centers of the brain.', 'tt0068677', '/v8VKM3SiVpzC6ueBScwnWYjZxCH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9603, 'Eat', False, True, False, False, 2014, 5, 'Novella McClure is like most struggling actresses in Los Angeles: she''s in her early 30s, her fake name sounded cooler ten years ago, and she hasn''t landed a role in three years. To top it all off, she''s developed a disturbing habit of eating her own flesh. Novella desperately tries to hide her strange condition from her motherly landlord, Eesha, and somewhat psychopathic best friend, Candice, but her body and mind continue to deteriorate in the depressing world of failed auditions and sketchy night clubs. Can a romantic relationship with her psychiatrist prevent her from self destruction? Or will her fatal habit continue to eat away at her?', 'tt3013018', '/aITe86bOUaoswzlNfHp4KyWXbPb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9604, 'Russell Brand: End the Drugs War', False, True, False, False, 2014, 7.2, 'In this personal journey for BBC Three, Russell Brand sets out to find out how other countries are tackling their problems of drug abuse and to explore how the framework of criminalization implicit in the ''war on drugs'' produces enormous harm in the treatment of addicts.', 'tt4286440', '/pfSkeqXGje6J43rzM3fbg7WAvzL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9606, 'Enemy of the Reich: The Noor Inayat Khan Story', False, True, False, False, 2014, 7.3, 'In 1943, Noor Inayat Khan was recruited as a covert operative into Winston Churchill''s Special Operations Executive. With an American mother and Indian Muslim father, she was an extremely unusual British agent. After her network collapsed, Khan became the only surviving radio operator linking the British to the French Resistance in Paris, coordinating the airdrop of weapons and agents, and the rescue of downed Allied fliers.', 'tt3088628', '/qLcvd2hha8HdYaqX7alHmWwBJMa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9613, 'Island of Death', False, True, False, False, 1976, 5.1, 'A British couple on a break on a small Greek Island, spreading terror beyond anything the Islanders could have ever imagined. Only stopping every once in a while to shag anything that moves, be it a man, woman or an animal. But will they go unpunished, or the inspector from London will be able to put an end to their killing spree?', 'tt0188527', '/nYLP7jiP5MYnf3LjZ5UAz2apfYj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9616, 'Lavalantula', False, True, False, False, 2015, 4.5, 'Giant lava breathing tarantulas – Lavalantulas – erupt out of ancient volcanos in the Santa Monica Mountains, raining death and destruction upon Los Angeles. With the City of Angels on the verge of incineration, only a washed up ‘90s action hero actor stands in the way of this monstrous swarm of bloodthirsty creatures who burn their victims alive.', 'tt4060576', '/fOcUwBJ2eOekwqBtJZncnrshtos.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9617, 'Ninja Wars', False, True, False, False, 1982, 5.8, 'A fortuneteller prophesizes that whoever marries the beautiful Ukio will become the ruler of the world. Naturally, this makes her very popular with those vying for power, particularly the insatiably greedy Lord Donjo. Only the ninja Jotaro can stop Donjo''s plan, but to do so, he must first battle the deadly team of "Devil Monks" serving the lord. Will Jotaro succeed in his mission? And if so, will Ukio marry him, thus making him the ruler of the world?', 'tt0089691', '/vudiVyhirl5d7qEMtAKKLbPf086.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9623, 'The Rift', False, True, False, False, 1990, 4.7, 'The satellite has vanished and the only clue is a trail leading to a small village nestled on the outskirts of forest line. As the team begins to investigate they quickly discover that something else came back from space, something not of this world. As their time runs out, the team must battle to unlock the terrifying truth behind the ill-fated mission.', 'tt4509840', '/nMIJv6LPodTvTPOAMoRYGm4fXHp.jpg');
