insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9628, 'The Clown at Midnight', False, True, False, False, 1999, 5.2, 'Seven teenagers are stalked by a murderous clown while refurbishing an old opera house.', 'tt0156413', '/ng8VJLgquIl5PNnHzslK5C7VZk5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9631, 'George Lopez: Tall, Dark & Chicano', False, True, False, False, 2009, 5.4, 'One of the premier comedic talents in the entertainment industry, George Lopez stars in his second HBO solo special. George Lopez: Tall, Dark &amp; Chicano is a live stand-up special performed in front of a packed arena crowd at the AT&amp;T Center in San Antonio, TX.', 'tt1508274', '/be6gNC20qqPjtAt88H75dnU0CZQ.jpg');
