insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (9749, 'The Kitchen', False, True, False, False, 2012, 5, 'Jennifer''s thirtieth birthday party is supposed to be a special day. But what starts out as a day of celebration quickly spirals into a most ill-fated day Jennifer wishes she could forget, in this ensemble comedy set entirely in a kitchen.', 'tt2011300', '/76GKE7k3AwWDUEj0dR5zfIYSY1V.jpg');
