insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10076, 'Andrew Dice Clay: Indestructible', False, True, False, False, 2012, 7.2, 'Just when you thought you where safe, the "indestructible" Andrew Dice Clay is back with a brand new stand-up comedy special - his first in 15 years - proving he is still the undisputed heavyweight comedy king. Filmed in Chicago at the Arcada Theater, Dice picks up where he left off, taking on every taboo topic from gay marriage to racial stereotypes to sexual exploits.', 'tt2605244', '/q7UyRK9R0bXBbUkL5Sw71Zau0pe.jpg');
