insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10087, 'Shadows & Lies', False, True, False, False, 2010, 4.3, 'The story of William Vincent as he recounts the eccentric and curious path that has brought him, at mortal risk, to New York City, after four years in exile, to rescue a woman he scarcely knows, Ann, from the vague crime syndicate that first brought them together.', 'tt1453403', '/d1u7IdoF7H1giuqjsmxqqXzmNHD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10088, 'The Keeper', False, True, False, False, 2004, 4.7, 'Paul Lamont, a corrections officer and law student, leads a comfortable if culturally bankrupt, middle-class existence. Lamont''s marriage is already in trouble when he bails out a mysterious Haitian, Jean Baptiste, in the belief that Baptiste has been wrongly accused. Baptiste insinuates himself in Lamont''s life and leads him on a journey of discovery. Lamont then finds that acts of conscience can have unforeseen consequences.', 'tt0113535', '/e3QjHPEMu208Qs49c0ZVv1Yr5dG.jpg');
