insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10485, 'Hospitalité', False, True, False, False, 2010, 6.5, 'In this black comedy the lives of a timid small-time printer and his young wife are turned inside out by the arrival of a stranger who moves in and takes over their world. Set in a village-like outpost in the heart of Tokyo, this is a wry commentary on Japanese xenophobia. Kiki Sugino heads a spritely ensemble cast.', 'tt1733147', '/uFbYV8EBDT4ApCtXuoZHdNdfIZt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10486, '29 Palms', False, True, False, False, 2002, 4.7, 'When a bag filled with money goes missing from a casino, the Hitman (Chris O''Donnell) must retrieve it. While he tracks the stash down, the bag changes hands numerous times, finding its way to the Drifter (Jeremy Davies) and the Waitress (Rachael Leigh Cook), among others. As the bag''s journey continues, more characters, including the Cop (Michael Rapaport) and the Sheriff (Keith David), get drawn into the winding crime tale, and the search becomes increasingly desperate.', 'tt0283090', '/hDNBHNJ0pu1ey6FRtJ0MwcU0Mcf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10487, 'The Legend of Seven Cutter', False, True, False, False, 2006, 6.2, 'Han-soo is a new transfer student at Seonggji High School, who is thought to be the legendary "Seven Cutter", a badass who beat up an entire gang, attracting the attention of school bully Sung-gi and his lackeys.', 'tt0798423', '/v9WfZkonNNoat4DCgnbA5wdCsCQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10494, 'Flu Bird Horror', False, True, False, False, 2008, 3.1, 'A group of teenagers go on a excursion to the mountains. There, they are attacked by birds infected with a lethal virus. When the teens reach a nearby village, the haunting birds start passing their virus on to all the dwellers.', 'tt1282045', '/wtkmyFE3U9ThIU1vmUDGQSK8o12.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10497, 'Creature', False, True, False, False, 1985, 4.3, 'An amphibious shark-like monster terrorizes an abandoned secret military base and the people who live on the island it is located on. A marine biologist, as well as several other people, try to stop it before it is too late...', 'tt0156205', '/bRVYB5BvwihQJMQjCL2cbm7FDxQ.jpg');
