insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10509, 'Solitary', False, True, False, False, 2009, 5.3, 'Sara (Amber Jaeger) suffers from agoraphobia, an obsessive fear of open spaces that renders her a virtual captive of her own home. When her husband, Mark (Kieron Elliott), disappears unexpectedly, Sara unravels emotionally and begins to panic. Turning to her estranged sister (Kristine Sullivan) and a psychiatrist (Andrew Qamar) for support, Sara soon begins to wonder whether the doctor is trying to help her or is plotting her demise.', 'tt1327763', '/fVihcDivL3pIbP6wNNMIOnPDkRj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10511, 'Rasputin', False, True, False, False, 2010, 5, 'Into an era seething with war and revolution, a man comes with an incredible power to heal a nation...or destroy it. Based on the true story of one of the most powerful and mysterious figures in Russian history.', 'tt0117442', '/wOH1tMYwTE1b8U7v2nwRqs0FQyi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10513, 'Katherine', False, True, False, False, 1975, 6.2, 'A harrowing look at the 60s and early 70s through the eyes of Katherine Alman, a wealthy debutante who slowly, but inexorably spirals down into a fight for the causes that shook a nation, leading a path to the underground life. Written by Miguel Cane', 'tt0073229', '/4HYuef3MEp0Wn7xiNCwpYfrIhFU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10517, 'Ski Party', False, True, False, False, 1965, 5.4, 'Remember the beach movies of the sixties? They''re back! Well, not quite, but since Avalon, Hickman, and even Funicello appear in this one, it sure feels right. This time, though, read snow instead of sand and you got it. Ski lodge, to be precise, and though the plot is somewhat inane, with the boys cross-dressing to discover the secret of a friend''s success with the girls, it''s still a somewhat fun outing.', 'tt0059726', '/r0cWq6UU8yXO6YjwU6pcc7aqkOr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10524, 'The Warrior and the Sorceress', False, True, False, False, 1984, 4.3, 'The mighty warrior, Kain, crosses the barren wastelands of the planet Ura, where two arch enemies, Zeg and the evil degenerate Balcaz, fight incessantly for control of the village''s only well. Kain sees his opportunity and announces that his sword is for hire... but his eyes stay clearly on the beautiful captive sorceress Naja, and his newly awakened purpose.', 'tt0088379', '/l6Fat5qyHlYcF2O3XNDcOF5va1a.jpg');
