insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10542, 'Sorority House Massacre', False, True, False, False, 1986, 4.5, 'A little girl''s brother kills the whole family but her (she escapes by hiding in the basement). He is committed, and she grows up with a new family, eventually going to college, where she joins a sorority. Due to a memory block, she doesn''t remember that the sorority house was her childhood house. Her brother senses her presence in the house and escapes so he can finish the job he was unable to complete.', 'tt0091990', '/yLUlHWqcLholK3pqKZRmRAZa1DP.jpg');
