insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10568, 'Dana Carvey: Squatting Monkeys Tell No Lies', False, True, False, False, 2008, 7.2, 'As always, Carvey never crosses the line into mean-spiritedness, but easily glides in and out of every impression &amp; expression, reminding us of the huge talent we came to know and love before he left the scene due to his heart condition. It''s a bittersweet irony that Dana Carvey is one comic who is ALL heart...', 'tt1251752', 'None');
