insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10574, 'Bed & Breakfast', False, True, False, False, 2010, 5, 'Bed and Breakfast is a romantic comedy about a saleswoman from a large department store in Rio who discovers she has inherited property in the wine country of California. She could never expect what she would find in "Webster", a small country town', 'tt1678040', '/33A14tBrCfz0fGcLOINbh3lqwBm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10576, 'Dogora', False, True, False, False, 1964, 5.7, 'Director Patrice Leconte weaves an impressionistic, sensorial tapestry of existence in and around early 21st-century Cambodia. Journeying through the country''s farmlands, factories, streets and rural villages, camera-in-hand, Leconte glimpses the people and the elements of the landscape that make Cambodia so culturally specific, and gently contrasts modes of life found there. Written by Nathan Southern', 'tt0400370', '/w6E7b0zMFbXnis8qg6L3kJZMZ15.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10581, 'Big Bad Mama II', False, True, False, False, 1987, 4.8, 'It''s 1934, and the evil local land baron forecloses on Angie''s place, and she and her two daughters must leave and continue their life of crime. A reporter witnesses their heist of a bank, and helps them become folk legends by writing a story about them. After a time the evil land baron wants to run for governor, and Angie and her daughters kidnap his son and turn him into a gangster in order to discredit his father and his run for governor.', 'tt0092652', '/ie54m6IXailnSq41KQeWgsrSbM0.jpg');
