insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10678, 'A Night In the Woods', False, True, False, False, 2011, 3.7, 'Brody, his girlfriend Kerry and their friend Leo go hiking in Dartmoor''s Wistman''s Woods, so named because of its legendary haunted past. That night jealousies, sexual tensions and strained relationships come to a head turning what should have been a peaceful camping adventure into a trip to terror. As collective paranoia reaches fever pitch it becomes clear that there is a much darker force at work in the ancient eerie surroundings. Who or what is after them? And can any one of them survive a night in the woods?', 'tt1718714', '/soF7QNbDYUgTUwPrElRDoiuIiEJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10683, 'Rent-a-Cop', False, True, False, False, 1987, 4.6, 'When call-girl Della gets caught in the middle of a drug bust at a hotel where she was meeting a trick, she is held hostage by a robber that busted in on the drug agents and the drug dealers. She gets rescued by vice cop Church who is accused of staging the aborted bust. Ex-ballplayer turned drug dealer Roger is in tight with corrupt vice cops and their superiors And the fireworks start popping.', 'tt0095977', '/vCRkBrTPZzBJubcUnkxUsbhL7Ro.jpg');
