insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10696, 'One-Eyed Monster', False, True, False, False, 2008, 4.2, 'In February 2007, ten people went to the remote mountains of Northern California to shoot an adult movie. What happened next was something no one expected, but everyone saw coming. Amber Benson, Charles Napier, and Ron Jeremy star in an homage to "Alien", "The Thing", and porn.', 'tt0988043', '/xXDmkwuWyQTjD8Tz7dJr93z9KdU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10703, 'The Tender Hook', False, True, False, False, 2008, 5.4, 'The story is about Iris'' rise to the apex of a love/power triangle that includes her roguish English lover, McHeath and Art, an earnest young boxer. Within the flawed moral landscape, each character struggles to establish their sovereignty.', 'tt0452692', '/t5F7LifqbvTOyLLN0BHVYpGbxji.jpg');
