insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10737, 'Dark House', False, True, False, False, 2014, 4.8, 'A young man sets out on a trip that takes him to an abandoned mansion that holds the origins of his dark family legacy.', 'tt1385543', '/higcYAgkTyfc9lSx9xfBYxB3iio.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10738, 'The Frankenstein Theory', False, True, False, False, 2013, 4.2, 'From the makers of The Last Exorcism comes a boldly original vision of horror. What if the most chilling novel of all time was actually based on a true account of a horrific experiment gone awry? When he is suspended from his university job for his outlandish ideas, Professor John Venkenheim leads a documentary film crew to the rim of the Arctic Circle in a desperate effort to vindicate his academic reputation. His theory: Mary Shelley''s ghastly story, "Frankenstein," is, in fact, a work of non-fiction disguised as fantasy. In the vast, frozen wilderness, Venkenheim and his team search for the legendary monster, a creature mired in mystery and drenched in blood. What they find is an unspeakable truth more terrifying than any fiction...a nightmare from which there is no waking.', 'tt2646378', '/kC8J8grw6G9EZfXun041hrVTKH5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10745, 'The Aviators', False, True, False, False, 2009, 6.5, 'A bird that wants to become a hero, a carrier pigeon with an endless list of achievements under his belt and a mouse inventor who want to fly at all costs are some of the characters in this animated film. Peace reigns in a farm located in the forests of Argonne (France), where the rooster no mercy to his son how to wake up and the sheep are all one. But running the year 1918, and the war between humans begins to touch them closely when an army captain pigeon lands in order to enlist some birds, among which strain the little bird.', 'tt1476250', '/drb87gsMh8gqvsjfOL39oaBsJ91.jpg');
