insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10755, 'Blood Freak', False, True, False, False, 1972, 3.7, 'A biker comes upon a girl with a flat tire and offers her a ride home. He winds up at a drug party with the girl''s sister, then follows her to a turkey farm owned by her father, a mad scientist. The father turns the biker into a giant turkey monster who goes after drug dealers.', 'tt0132888', '/kTrfL4aKEbN0M7uXvWOThZHJlfY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10758, 'Dillinger and Capone', False, True, False, False, 1995, 5.2, 'In 1934, J. Edgar Hoover and the boys made headlines for mowing down John Dillinger in a hail of bullets outside Chicago''s Biograph theater. But in fact, according to this Jon Purdy gangster thriller, the Feds iced Dillinger''s brother. Fast-forward five years, when mobster kingpin Al Capone (F. Murray Abraham) gives the real Dillinger (Martin Sheen) an offer he can''t refuse: rob millions from a secluded vault or watch his wife and child get whacked.', 'tt0112869', '/v7D9fIg2DqsrrJKnAV3gcACr859.jpg');
