insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10852, 'Bayou Blue', False, True, False, False, 2011, 6.2, 'From 1997 to 2006, serial killer Ronald Dominique raped and killed twenty-three men in poverty- stricken Southeastern Louisiana. Difficulties in apprehending Dominique ranged from the underfunding of law enforcement to a lack of family advocacy for the victims, to the general distraction by other catastrophes such as Hurricane Katrina. Bayou Blue meditates on the decay of a community.', 'tt2106331', '/pilO3O9Vz4dITWyaNTtRmFtnVQB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (10863, 'Suhaag', False, True, False, False, 1994, 5.7, 'Collegian Ajay Sharma was born on 2nd April 1968 and lives in Bombay with his widowed mom, Asha. He is friendly with fellow-collegian, the wealthy Raj Sinha, and both often end up getting beat-up by goons and Madhu (Raj''s girlfriend) respectively. After they graduate from college, Raj''s maternal uncle asks Ajay to bring his birth certificate so that he could make his passport and find work for him in a department store in Canada. Ajay goes home to look for his birth certificate, but is unable to locate. His mother makes a show of helping him, but instead tears a document and tosses it outside. Ajay puts the document together and finds out that his real name is Ajay Malhotra, his father, Dr. Ravi Malhotra, is still alive, serving a life sentence in prison for killing and stealing a patient''s organs. Ajay questions his mother and finds out that his dad was framed by the hospital owner, Rai Bahadur. He and Asha meet Ravi in prison and swear to avenge his humiliation.', 'tt0111308', '/y47kzea1h3LkzBHTwMEftbiY8eX.jpg');
