insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11109, 'Lisa Lampanelli: Back to the Drawing Board', False, True, False, False, 2015, 5.9, 'Brash, bold and never afraid to go blue, comedian Lisa Lampanelli offers a raucous and raunchy performance in her first stand-up special for EPIX. Taped at the Music Hall in Tarrytown, New York, the set includes Lampanelli’s signature brand of insult comedy as well as her personal experiences with weight loss and divorce.', 'tt4566712', '/lazS9AjavbK57L0GhBv3F3r7RaC.jpg');
