insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11171, 'Guest in the House', False, True, False, False, 1944, 6.2, 'Evelyn (Anne Baxter), an emotionally vulnerable and unstable woman, stays at the home of her doctor Dan Proctor (Scott McKay). There she meets and falls in love with his brother, Douglas (Ralph Bellamy), who is happily married to Ann (Ruth Warrick). Evelyn then sets forth to break up the happy marriage and win the love of Douglas -- with tragic results.', 'tt0036886', '/vr4qZnS4DFbwrGK1BVwt7fbd24j.jpg');
