insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11225, 'Shark Attack 2', False, True, False, False, 2001, 3.1, 'A biological experiment goes bad, this time releasing a gaggle of mutated great white sharks with a taste for human flesh. Soon enough, shark expert Nick West is on the case, leading a crew to study them and eventually bring them back into captivity. West''s plans hit a snag, however, when Australian shark hunter Roy Bishop is called in to wipe out the fishy menace.', 'tt0222355', '/mnzeIraE5HhY8Bg3wZE7spb0aVW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11228, 'The Count of Monte Cristo', False, True, False, False, 1996, 5.4, 'Edmond Dantes is falsely accused by those jealous of his good fortune, and is sentenced to spend the rest of his life in the notorious island prison, Chateau d''If. While imprisoned, he meets the Abbe Faria, a fellow prisoner whom everyone believes to be mad. The Abbe tells Edmond of a fantastic treasure hidden away on a tiny island, that only he knows the location of. After many years in prison, the old Abbe dies, and Edmond escapes disguised as the dead body. Now free, Edmond must find the treasure the Abbe told him of, so he can use the new-found wealth to exact revenge on those who have wronged him.', 'tt0046415', '/y9B4oMNYOgv7pzvDGecCCILgrne.jpg');
