insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11263, 'Under the Boardwalk', False, True, False, False, 1989, 5.5, 'It is the final weekend of summer and a group of Californian teenagers are looking forward to an upcoming surf contest. Rival gangs the ''Vals'' and the ''Lowks'' are confident that they will take home the trophy, but things become complicated when Reef Yorpin - leader of the Lawks - discovers his sister Allie has fallen in love with ''Val'' surfer Nick after meeting at a beach party.', 'tt0098557', '/9ULS3z6RLDYHsoPUWBKf70h6XUF.jpg');
