insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11394, 'Futuresport', False, True, False, False, 1998, 4.1, 'In the not too distant future, a new deadly sport is the only way to stop a war that will kill millions. Fix (Snipes) is the creator of the sport, but the spotlight has always been on Ramzey (Cain), this hothead must use this game to stop the Revolution.', 'tt0158409', '/SPJXju2Wq1VmjsVYU2aUSP4eFX.jpg');
